declare src_res CHARACTER VARYING;
        dest_before CHARACTER VARYING;
        dest_after CHARACTER VARYING;
BEGIN
  if dest_lob is null or src_lob is null or amount is null or dest_offset is null or src_offset is null then raise exception 'invalid input parameters';
  elsif src_offset < 1 or dest_offset < 1 then raise exception 'invalid input parameters';
  elsif amount < 1 then raise exception 'invalid input parameters';
  end if;

  src_res := substring(src_lob from src_offset for amount);
  dest_before := substring(dest_lob, 1, dest_offset - 1);
  dest_after := substring(dest_lob from length(src_res) + length(dest_before) + 1);
  dest_lob := dest_before || src_res || dest_after;
END;

declare matches integer;
        ind integer;
begin
  /*parameters validation*/
  if lob_loc is null or pattern is null or off_set is null or nth is null then 
    return null;
  elsif (off_set < 1) or (nth < 1) then 
    return null;
  end if;

  if (off_set = 1) and (nth = 1) then 
    return strpos(lob_loc, pattern);
   else 
     matches := 0;
     for i in off_set..length(lob_loc) loop
       if substring(lob_loc, i, length(pattern)) = pattern then 
         ind := i;
	 matches := matches + 1;
	 if matches = nth then 
	   return ind;
	 end if;
       end if;           
     end loop;
     return 0;
  end if;
end;

-- ORACLE DBMS_LOB.LOBMAXSIZE
--  lobmaxsize    CONSTANT INTEGER        := 18446744073709551615;
SELECT 18446744073709551615

BEGIN
    lockhandle := lower(lockname);
END;

declare
  l_unlock boolean := true;
  l_exit_code int;
BEGIN
    if aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_lock', concat('lockmode_',id))::INTEGER=6 /* lockmode: X_MODE = Exclusive */ then
      l_unlock := pg_advisory_unlock(id);
    else
      l_unlock := pg_advisory_unlock_shared(id);
    end if;

    l_exit_code := case when l_unlock then 0 -- Success
                        else 5 -- Illegal lock handle
                end;
    return l_exit_code;
END;

declare
  l_getlock boolean := true;
  l_result  int;
BEGIN
    /* lockmode is implemented only for values
     *   6 - Exclusive
     *   not 6 - Shared
     *
     * timeout is implemented only for values
     *   0 - no wait
     *   not 0 - wait untill free
     */
    perform aws_oracle_ext.set_package_variable('aws_oracle_ext', 'dbms_lock', concat('lockmode_', id), lockmode);

    case when not release_on_commit and lockmode = 6 and timeout = 0 then l_getlock := pg_try_advisory_lock(id);
         when not release_on_commit and lockmode = 6 and timeout != 0 then perform pg_advisory_lock(id);
         when not release_on_commit and lockmode != 6 and timeout = 0 then l_getlock := pg_try_advisory_lock_shared(id);
         when not release_on_commit and lockmode != 6 and timeout != 0 then perform pg_advisory_lock_shared(id);
         when release_on_commit and lockmode = 6 and timeout = 0 then l_getlock := pg_try_advisory_xact_lock(id);
         when release_on_commit and lockmode = 6 and timeout != 0 then perform pg_advisory_xact_lock(id);
         when release_on_commit and lockmode != 6 and timeout = 0 then l_getlock := pg_try_advisory_xact_lock_shared(id);
         when release_on_commit and lockmode != 6 and timeout != 0 then perform pg_advisory_xact_lock_shared(id);
         else l_getlock := false;
    end case;

    l_result := case when l_getlock then 0 -- Success
                     else 1 -- Timeout
                end;
    return l_result;
END;

BEGIN
  return decrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  decrypted_data := decrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  return encrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  encrypted_data := encrypt(input, key, 'des-cbc/pad:none');
END;

BEGIN
  return crypt(seed, gen_salt('des'));
END;

BEGIN
  key := crypt(seed, gen_salt('des'));
END;

BEGIN
  return md5(input);
END;

BEGIN
  checksum := md5(input);
END;

BEGIN
  return md5(input_string);
END;

BEGIN
  checksum_string := md5(input_string);
END;

BEGIN
    PERFORM aws_oracle_ext.dbms_random$seed(p_seed);
END;

DECLARE
    v_u DOUBLE PRECISION;
    v_v DOUBLE PRECISION;
    v_s DOUBLE PRECISION;
BEGIN
    LOOP
        -- Range -1.0 <= u | v < 1.0
        v_u = random() * 2 - 1;
        v_v = random() * 2 - 1;
        v_s = power(v_u, 2) + power(v_v, 2);

        CONTINUE WHEN NOT (v_s != 0 AND v_s < 1);
        RETURN sqrt(-2 * ln(v_s) / v_s) * v_u;
    END LOOP;
END;

BEGIN
    RETURN round((random() * 4294967296 - 2147483648)::NUMERIC, 0);
END;

DECLARE
    v_seedsign INTEGER := sign(p_seed);
    v_seedstr VARCHAR := rpad(to_char(abs(p_seed), 'FM9999999999'), 15, '0');
BEGIN
    PERFORM setseed(to_number(concat('0.', v_seedstr), '9.FM999999999999999999') * v_seedsign);
END;

DECLARE
    v_seedstr TEXT := substring(translate(upper(md5(p_seed)), 'ABCDEF', '123456'), 1, 15);
BEGIN
    PERFORM setseed(to_number(concat('0.', v_seedstr), '9.FM999999999999999999'));
END;

BEGIN
    PERFORM setseed(random());
END;

BEGIN
    RETURN random() * (p_high - p_start) + p_start;
END;

declare
  l_namespace text;
  l_attribute text;
begin	
   l_namespace := aws_oracle_ext.format_identifier_case(namespace);
   l_attribute := aws_oracle_ext.format_identifier_case(attribute);

   if l_namespace is null then 
   		raise exception 'input value for argument namespace is not valid';    
   end if;

  if l_namespace = 'CLIENTCONTEXT' then
    raise exception 'CLIENTCONTEXT namespace will be cleared automatically in the end of the session. Can not be cleared manually';    
  end if;

  case aws_oracle_ext.get_context_type(l_namespace) 
    when 'ACCESSED GLOBALLY' then 
    	delete from aws_oracle_context.context_global cg
    	where cg.context_namespace = l_namespace
    	and coalesce(cg.cliend_id, '') = coalesce($2, '')
    	and cg.attribute_name = coalesce(l_attribute, cg.attribute_name);         		        	
    when 'ACCESSED LOCALLY' then 
    	delete from context_local where context_namespace = l_namespace and coalesce(attribute_name, '') = coalesce(l_attribute, '');
    else raise exception 'Unsupported context type';   
  end case;

end;

begin
  perform aws_oracle_ext.set_extpack_variable
    (
	  variable_name => 'CLIENT_IDENTIFIER', 
	  variable_value => null::text
	);
END;

BEGIN
  return exists (select 1 from information_schema.enabled_roles where role_name = rolename);
END;

BEGIN
  return ((select state from pg_stat_activity where pid = uniqueid) = 'active'::text);
END;

'use strict';
  if (typeof(require) !== 'function') {
    const initColl = plv8.find_function("aws_oracle_ext.plv8_require");
    initColl();
  }
  const cursors = plv8.execute(`
SELECT pc.name FROM pg_catalog.pg_cursors pc where name like '%$o'
`);
  for (let i = 0; i < cursors.length; i++) {
    try{
      plv8.subtransaction(function(){
        plv8.execute(`CLOSE ${cursors[i].name}`);
      });
    } catch(e) {
    }
  }
  for (let key of Object.keys(aws_oracle_ext)) {
    if (key === 'aws_oracle_ext') continue;
    delete aws_oracle_ext[key];
  }

DECLARE
  l_key text;
  l_context_type text; 
  l_crypted text;
  l_interval text;
  l_namespace text;
  l_attribute text;
BEGIN
  if namespace is null or attribute is null then 
    raise exception 'invalid input values';  
  end if; 


  l_namespace := aws_oracle_ext.format_identifier_case(namespace);
  l_attribute := aws_oracle_ext.format_identifier_case(attribute);

  if l_namespace = 'CLIENTCONTEXT' then 
    perform  aws_oracle_ext.set_extpack_variable
                                (
                                 variable_name => l_namespace||'_'||l_attribute, 
                                 variable_value => value
                                );
  else 
   	perform aws_oracle_ext.init_context_struct();  
    l_key := aws_oracle_ext.get_context_param('key');  
    l_context_type := aws_oracle_ext.get_context_type(l_namespace);   
    l_crypted := crypt(value || ':' || l_key, gen_salt('bf'));   


   	case upper(l_context_type) 
	  	when 'ACCESSED LOCALLY' then 
	  		begin
				insert into context_local(
				  context_namespace, 
				  attribute_name, 
				  attribute_value, 
				  user_name, 
				  cliend_id, 
				  signature
				)
				values (
				  l_namespace, 
				  l_attribute, 
				  $3, 
				  $4, 
				  coalesce($5, aws_oracle_ext.sys_context_userenv('CLIENT_IDENTIFIER')), 
				  l_crypted
				) 
				on conflict (context_namespace, attribute_name) do 
				update set 
					attribute_value = excluded.attribute_value,
					signature = excluded.signature;
		  	end;
	  	when 'ACCESSED GLOBALLY' then	  
	  		begin

				delete from aws_oracle_context.context_global 
			    where backend_start <= now()::timestamp - (coalesce(aws_oracle_ext.get_context_param('auto_clean_interval'), '1 day'))::interval;

				insert into aws_oracle_context.context_global(
				  context_namespace, 
				  attribute_name, 
				  attribute_value, 
				  user_name, 
				  cliend_id, 
				  signature
				)
				values (
				  l_namespace, 
				  l_attribute, 
				  $3, 
				  $4, 
				  coalesce($5, aws_oracle_ext.sys_context_userenv('CLIENT_IDENTIFIER')), 
				  l_crypted
				) 
				on conflict (context_namespace, attribute_name, cliend_id) do 
				update set 
					attribute_value = excluded.attribute_value,
					signature = excluded.signature;
		  	end;  
	  	else 
			-- INITIALIZED EXTERNALLY, INITIALIZED GLOBALLY  	
	  		raise exception 'unsupported context type: %', l_context_type;
	  end case;    
  end if;

  exception 
    when no_data_found then raise exception 'Context % is not found', l_namespace;  
END;

begin
  perform  aws_oracle_ext.set_extpack_variable
   (
      variable_name => 'CLIENT_IDENTIFIER', 
      variable_value => client_id
   ); 
END;

begin
  perform  aws_oracle_ext.set_extpack_variable
   (
      variable_name => upper(param), 
      variable_value => val
   ); 


  if upper(param) = 'NLS_LANGUAGE' then

    perform  aws_oracle_ext.set_extpack_variable
     (
        variable_name => 'NLS_DATE_LANGUAGE', 
        variable_value => val
     );    


    perform  aws_oracle_ext.set_extpack_variable
     (
        variable_name => 'NLS_SORT', 
        variable_value => val
     );    

  end if;
END;

DECLARE
    v_var_pos INTEGER;
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_var_size INTEGER;
    v_value_size INTEGER;
    v_is_fixed SMALLINT;
    v_out_base_type VARCHAR;
    v_out_data_type VARCHAR := upper(pg_typeof(p_var_value)::VARCHAR);
BEGIN
    /* Requires next functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open.
    */

    -- Exit from procedure if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty (null)
    ELSEIF (p_var_name IS NULL) THEN
        RAISE null_value_not_allowed;
    END IF;

    IF (v_out_data_type IN ('TEXT',
                            'CHARACTER',
                            'CHARACTER VARYING'))
    THEN
        v_out_base_type := 'TEXT';
        v_chr_val := p_var_value::TEXT;
        v_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);
        v_value_size := coalesce(p_value_size, v_var_size);
    ELSIF (v_out_data_type IN ('REAL',
                               'BIGINT',
                               'INTEGER',
                               'NUMERIC',
                               'DECIMAL',
                               'SMALLINT',
                               'DOUBLE PRECISION'))
    THEN
        v_out_base_type := 'NUMERIC';
        v_num_val := p_var_value::NUMERIC;
    ELSIF (v_out_data_type = 'MONEY') THEN
        v_out_base_type := 'MONEY';
        v_money_val := p_var_value;
    ELSIF (v_out_data_type IN ('DATE',
                               'TIMESTAMP WITHOUT TIME ZONE'))
    THEN
        v_out_base_type := 'TIMESTAMP WITHOUT TIME ZONE';
        v_tstamp_val := p_var_value::TIMESTAMP WITHOUT TIME ZONE;
    ELSIF (v_out_data_type = 'TIMESTAMP WITH TIME ZONE') THEN
        v_out_base_type := 'TIMESTAMP WITH TIME ZONE';
        v_tstamptz_val := p_var_value;
    ELSIF (v_out_data_type = 'INTERVAL') THEN
        v_out_base_type := 'INTERVAL';
        v_interval_val := p_var_value;
    ELSIF (v_out_data_type = 'BOOLEAN') THEN
        v_out_base_type := 'BOOLEAN';
        v_bool_val := p_var_value;
    ELSIF (v_out_data_type = 'XML') THEN
        v_out_base_type := 'XML';
        v_xml_val := p_var_value;
    ELSIF (v_out_data_type = 'BYTEA') THEN
        v_out_base_type := 'BYTEA';
        v_bin_val := p_var_value;
        v_var_size := coalesce(octet_length(p_var_value), 0);
        v_value_size := coalesce(p_value_size, v_var_size);
    ELSE RETURN;
    END IF;

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:[[:space:]]*\$[[:digit:]]+[[:space:]]*(?:\|)?)+)*[[:space:]]*\:?([[:alnum:]_]+)[[:space:]]*$', 'gi');
    v_var_info[1] := rtrim(v_var_info[1], '|');

    -- Determining next variable position
    SELECT coalesce(MAX(var_pos), 0) + 1
      INTO v_var_pos
      FROM dbms_sql$bind_variable
     WHERE cursor_id = p_cursor_id;

    -- Binding variable to cursor (identified by cursor_id)
    INSERT INTO dbms_sql$bind_variable (cursor_id,
                                        var_pos,
                                        var_name,
                                        var_label,
                                        data_type,
                                        base_type,
                                        var_size,
                                        value_size,
                                        is_fixed,
                                        chr_val,
                                        num_val,
                                        money_val,
                                        tstamp_val,
                                        tstamptz_val,
                                        interval_val,
                                        bool_val,
                                        xml_val,
                                        bin_val)
         VALUES (p_cursor_id,
                 v_var_pos,
                 v_var_info[2], -- Variable name
                 v_var_info[1], -- Variable label list
                 v_out_data_type,
                 v_out_base_type,
                 v_var_size,
                 v_value_size,
                 v_is_fixed,
                 v_chr_val,
                 v_num_val,
                 v_money_val,
                 v_tstamp_val,
                 v_tstamptz_val,
                 v_interval_val,
                 v_bool_val,
                 v_xml_val,
                 v_bin_val)
    ON CONFLICT (cursor_id, var_name)
    DO UPDATE SET var_label = v_var_info[1],
                  data_type = v_out_data_type,
                  base_type = v_out_base_type,
                  var_size = v_var_size,
                  value_size = v_value_size,
                  is_fixed = v_is_fixed,
                  chr_val = v_chr_val,
                  num_val = v_num_val,
                  money_val = v_money_val,
                  tstamp_val = v_tstamp_val,
                  tstamptz_val = v_tstamptz_val,
                  interval_val = v_interval_val,
                  bool_val = v_bool_val,
                  xml_val = v_xml_val,
                  bin_val = v_bin_val;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Variable name is empty (null)
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Bind variable name is empty.',
                    DETAIL := 'Could not bind a value to a variable without name. Oracle error ''ORA-01006''.',
                    HINT := 'Pass in an existing variable name in order to bind a value to it.';
END;

DECLARE
    v_var_pos INTEGER;
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_var_size INTEGER;
    v_value_size INTEGER;
    v_datetime_mask VARCHAR;
    v_is_fixed SMALLINT := 1;
    v_out_base_type VARCHAR := 'TEXT';
    v_var_datatype VARCHAR := 'CHARACTER VARYING';
    v_out_data_type VARCHAR := upper(pg_typeof(p_var_value)::VARCHAR);
BEGIN
    /* Requires next functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open.
    */

    -- Exit from procedure if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty (null)
    ELSEIF (p_var_name IS NULL) THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Casting input value to a character string
    IF (v_out_data_type IN ('TEXT',
                            'CHARACTER',
                            'CHARACTER VARYING'))
    THEN
        v_var_datatype := v_out_data_type;
        v_chr_val := p_var_value::TEXT;
        v_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);
    ELSIF (v_out_data_type IN ('XML',
                               'MONEY',
                               'REAL',
                               'BIGINT',
                               'INTEGER',
                               'NUMERIC',
                               'DECIMAL',
                               'SMALLINT',
                               'DOUBLE PRECISION',
                               'INTERVAL'))
    THEN
        v_chr_val := p_var_value::TEXT;
    ELSIF (v_out_data_type IN ('DATE',
                               'TIMESTAMP WITHOUT TIME ZONE',
                               'TIMESTAMP WITH TIME ZONE'))
    THEN
        v_datetime_mask := CASE v_outvar_datatype
                              WHEN 'DATE' THEN 'DD-MON-YY'
                              ELSE 'DD-MON-YY HH24:MI:SS.US'
                           END;

        v_chr_val := to_char(p_var_value, v_datetime_mask);
    ELSIF (v_out_data_type = 'BYTEA') THEN
        v_var_datatype := 'TEXT';
        v_chr_val := upper(encode(p_var_value, 'hex'));
    ELSE RETURN;
    END IF;

    v_var_size := coalesce(v_var_size, coalesce(char_length(v_chr_val), 0));
    v_value_size := coalesce(p_value_size, v_var_size);

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:[[:space:]]*\$[[:digit:]]+[[:space:]]*(?:\|)?)+)*[[:space:]]*\:?([[:alnum:]_]+)[[:space:]]*$', 'gi');
    v_var_info[1] := rtrim(v_var_info[1], '|');

    -- Determining next variable position
    SELECT coalesce(MAX(var_pos), 0) + 1
      INTO v_var_pos
      FROM dbms_sql$bind_variable
     WHERE cursor_id = p_cursor_id;

    -- Binding variable to cursor (identified by cursor_id)
    INSERT INTO dbms_sql$bind_variable (cursor_id,
                                        var_pos,
                                        var_name,
                                        var_label,
                                        data_type,
                                        base_type,
                                        var_size,
                                        value_size,
                                        is_fixed,
                                        chr_val)
         VALUES (p_cursor_id,
                 v_var_pos,
                 v_var_info[2], -- Variable name
                 v_var_info[1], -- Variable label list
                 v_var_datatype,
                 v_out_base_type,
                 v_var_size,
                 v_value_size,
                 v_is_fixed,
                 v_chr_val)
    ON CONFLICT (cursor_id, var_name)
    DO UPDATE SET var_label = v_var_info[1],
                  data_type = v_var_datatype,
                  base_type = v_out_base_type,
                  value_size = v_value_size,
                  is_fixed = v_is_fixed,
                  chr_val = v_chr_val;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Variable name is empty (null)
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Bind variable name is empty.',
                    DETAIL := 'Could not bind a value to a variable without name. Oracle error ''ORA-01006''.',
                    HINT := 'Pass in an existing variable name in order to bind a value to it.';
END;

BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - set_package_variable.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id::INTEGER)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Deleting related data from all temp tables
    DELETE FROM dbms_sql$cursor
          WHERE cursor_id = p_cursor_id::INTEGER;

    -- Cleaning cursor metadata (JSON)
    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', -- schema name
                                                'dbms_sql', -- package name
                                                concat('cursor_id_', p_cursor_id, '_metadata'), -- context "cursor_id_xxx"
                                                NULL::VARCHAR); -- cursor metadata JSON
    p_cursor_id := NULL;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcol_data JSONB;
    v_col_size INTEGER;
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_col_data_type VARCHAR;
    v_col_base_type VARCHAR;
    v_out_base_type VARCHAR;
    v_out_data_type VARCHAR := upper(pg_typeof(p_column_val)::VARCHAR);
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Getting dynamic SQL statement defined columns info (JSON)
    v_defcol_data := (v_cur_metadata #> concat('{defcols_data, ', 'col_', p_column_pos, '}')::TEXT[])::JSONB;

    v_col_data_type := (v_defcol_data ->> 'data_type')::VARCHAR; -- Column data type
    v_col_base_type := (v_defcol_data ->> 'base_type')::VARCHAR; -- Column base data type
    v_col_size := (v_defcol_data ->> 'col_size')::INTEGER; -- Column size in characters

    -- Determining base data type of passed variable
    v_out_base_type :=
        CASE
           WHEN v_out_data_type IN ('TEXT',
                                    'CHARACTER',
                                    'CHARACTER VARYING') THEN 'TEXT'
           WHEN v_out_data_type IN ('REAL',
                                    'BIGINT',
                                    'INTEGER',
                                    'NUMERIC',
                                    'DECIMAL',
                                    'SMALLINT',
                                    'DOUBLE PRECISION') THEN 'NUMERIC'
           WHEN v_out_data_type = 'MONEY' THEN 'MONEY'
           WHEN v_out_data_type IN ('DATE',
                                    'TIMESTAMP WITHOUT TIME ZONE') THEN 'TIMESTAMP WITHOUT TIME ZONE'
           WHEN v_out_data_type = 'TIMESTAMP WITH TIME ZONE' THEN 'TIMESTAMP WITH TIME ZONE'
           WHEN v_out_data_type = 'INTERVAL' THEN 'INTERVAL'
           WHEN v_out_data_type = 'BOOLEAN' THEN 'BOOLEAN'
           WHEN v_out_data_type = 'XML' THEN 'XML'
           WHEN v_out_data_type = 'BYTEA' THEN 'BYTEA'
        END;

    -- Default vars states
    p_column_val := NULL;
    p_column_err := 0;
    p_actual_len := 0;

    -- Column position should be declared in SELECT list
    IF (v_col_base_type IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Check if OUT argument base type matches declared column base type
    ELSIF (substring(v_out_base_type, 1, 14) != substring(v_col_base_type, 1, 14)) THEN
        RAISE datatype_mismatch;
    -- Variable should not be declared using "dbms_sql$define_column_char"
    ELSIF ((v_defcol_data ->> 'is_fixed')::SMALLINT = 1) THEN
        RAISE most_specific_type_mismatch;
    -- If current row not specified, then nothing to return
    ELSIF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RETURN;
    END IF;

    /* Extracting row data from execution results
       table "dbms_sql$recordset"
       (by cursor id, row number, column position)
    */
    SELECT chr_val,
           num_val,
           money_val,
           tstamp_val,
           tstamptz_val,
           interval_val,
           bool_val,
           xml_val,
           bin_val
      INTO v_chr_val,
           v_num_val,
           v_money_val,
           v_tstamp_val,
           v_tstamptz_val,
           v_interval_val,
           v_bool_val,
           v_xml_val,
           v_bin_val
      FROM dbms_sql$recordset
     WHERE cursor_id = p_cursor_id
       AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
       AND column_pos = p_column_pos;

    -- Casting data type and copying value into the OUT argument
    IF (v_out_base_type = 'TEXT') THEN
        p_actual_len := coalesce(char_length(v_chr_val), 0);

        IF (v_col_size IS NULL) THEN
            p_column_val := v_chr_val;
        ELSE
            p_column_val := substring(v_chr_val, 1, v_col_size);
            IF (v_col_size != 0 AND v_col_size < p_actual_len)
            THEN
                p_column_err := 1406;
            END IF;
        END IF;
    ELSIF (v_out_base_type = 'NUMERIC') THEN
        p_column_val := v_num_val;
        p_actual_len := CASE
                           WHEN v_col_data_type = 'SMALLINT' THEN 2
                           WHEN v_col_data_type IN ('INTEGER', 'REAL') THEN 4
                           ELSE 8
                        END;
    ELSIF (v_out_base_type = 'MONEY') THEN
        p_column_val := v_money_val;
        p_actual_len := coalesce(char_length(v_money_val::VARCHAR), 0);
    ELSIF (v_out_base_type = 'TIMESTAMP WITHOUT TIME ZONE') THEN
        IF (v_col_base_type = 'TIMESTAMP WITH TIME ZONE') THEN
            p_column_val := v_tstamptz_val::TIMESTAMP WITHOUT TIME ZONE;
        ELSE
            p_column_val := v_tstamp_val;
        END IF;

        IF (v_col_data_type = 'DATE') THEN
            p_actual_len := 9;
        ELSE
            p_actual_len := 35;
        END IF;
    ELSIF (v_out_base_type = 'TIMESTAMP WITH TIME ZONE') THEN
        IF (v_col_base_type = 'TIMESTAMP WITHOUT TIME ZONE') THEN
            p_column_val := v_tstamp_val::TIMESTAMP WITH TIME ZONE;
        ELSE
            p_column_val := v_tstamptz_val;
        END IF;

        p_actual_len := 35;
    ELSIF (v_out_base_type = 'INTERVAL') THEN
        p_column_val := v_interval_val;
        p_actual_len := coalesce(char_length(v_interval_val::VARCHAR), 0);
    ELSIF (v_out_base_type = 'BOOLEAN') THEN
        p_column_val := v_bool_val;
        p_actual_len := coalesce(char_length(v_bool_val::VARCHAR), 0);
    ELSIF (v_out_base_type = 'XML') THEN
        p_column_val := v_xml_val;
        p_actual_len := coalesce(char_length(v_xml_val::TEXT), 0);
    ELSIF (v_out_base_type = 'BYTEA') THEN
        p_column_val := substring(v_bin_val from 1 for v_col_size);
        p_actual_len := coalesce(octet_length(v_bin_val), 0);
    END IF;

    IF (p_column_val IS NULL) THEN
        p_column_err := 1405;
    END IF;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Column position is not declared in SELECT list
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Column position not declared in SELECT list.',
                    DETAIL := 'Supplied column position is NULL or zero. Oracle error ''ORA-01007: column not in select list''.',
                    HINT := format('Make sure that supplied column position was previously declared by ''dbms_sql$define_column'' procedure for cursor_id (%s).', p_cursor_id);
    -- OUT argument base type doesn't match declared column base type
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared type of column (%s), cursor_id (%s).', p_column_pos, p_cursor_id),
                    DETAIL := format('OUT argument type %s, column type %s. Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable''.',
                                     v_out_data_type, v_col_base_type),
                    HINT := format('Make sure that supplied OUT argument type match that for cursor_id (%s) column (%s).', p_cursor_id, p_column_pos);
    -- Variable was declared using "dbms_sql$define_column_char"
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared column type, cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable',
                    HINT := format('Try to use ''dbms_sql$column_value_char'' procedure to get column (%s) value for cursor_id (%s).',
                                   p_column_pos, p_cursor_id);
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcol_data JSONB;
    v_curcol_data JSONB;
    v_col_size INTEGER;
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_col_data_type VARCHAR;
    v_datetime_mask VARCHAR;
    v_out_var_size INTEGER;
    v_out_data_type VARCHAR := upper(pg_typeof(p_column_val)::VARCHAR);
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Getting dynamic SQL statement defined and actual columns info (JSON)
    v_defcol_data := (v_cur_metadata #> concat('{defcols_data, ', 'col_', p_column_pos, '}')::TEXT[])::JSONB;
    v_curcol_data := (v_cur_metadata #> concat('{curcols_data, ', 'col_', p_column_pos, '}')::TEXT[])::JSONB;

    v_col_size := (v_defcol_data ->> 'col_size')::INTEGER; -- Column size in characters
    v_col_data_type := (v_curcol_data ->> 'data_type')::VARCHAR; -- Column (actual) data type

    -- Default vars states
    p_column_err := 0;
    p_actual_len := 0;

    -- Column position should be declared in SELECT list
    IF ((v_defcol_data ->> 'is_fixed') IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Variable should not be declared using "dbms_sql$define_column"
    ELSIF ((v_defcol_data ->> 'is_fixed')::SMALLINT != 1) THEN
        RAISE most_specific_type_mismatch;
    -- If current row not specified, then nothing to return
    ELSIF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RETURN;
    END IF;

    /* Extracting row data from execution results
       table "dbms_sql$recordset"
       (by cursor id, row number, column position)
    */
    SELECT chr_val,
           num_val,
           money_val,
           tstamp_val,
           tstamptz_val,
           interval_val,
           bool_val,
           xml_val,
           bin_val
      INTO v_chr_val,
           v_num_val,
           v_money_val,
           v_tstamp_val,
           v_tstamptz_val,
           v_interval_val,
           v_bool_val,
           v_xml_val,
           v_bin_val
      FROM dbms_sql$recordset
     WHERE cursor_id = p_cursor_id
       AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
       AND column_pos = p_column_pos;

    -- Casting column data to a character string
    IF (v_col_data_type IN ('REAL',
                            'BIGINT',
                            'INTEGER',
                            'NUMERIC',
                            'DECIMAL',
                            'SMALLINT',
                            'DOUBLE PRECISION'))
    THEN
        v_chr_val := v_num_val::TEXT;
    ELSIF (v_col_data_type = 'MONEY') THEN
        v_chr_val := v_money_val::TEXT;
    ELSIF (v_col_data_type IN ('DATE',
                               'TIMESTAMP WITHOUT TIME ZONE'))
    THEN
        v_datetime_mask := CASE v_col_data_type
                              WHEN 'DATE' THEN 'DD-MON-YY'
                              ELSE 'DD-MON-YY HH24:MI:SS.US'
                           END;

        v_chr_val := to_char(v_tstamp_val, v_datetime_mask);
    ELSIF (v_col_data_type = 'TIMESTAMP WITH TIME ZONE') THEN
        v_chr_val := to_char(tstamptz_val, 'DD-MON-YY HH24:MI:SS.US');
    ELSIF (v_col_data_type = 'INTERVAL') THEN
        v_chr_val := v_interval_val::TEXT;
    ELSIF (v_col_data_type = 'BOOLEAN') THEN
        v_chr_val := CASE
                        WHEN v_bool_val THEN 'TRUE'
                        ELSE 'FALSE'
                     END;
    ELSIF (v_col_data_type = 'XML') THEN
        v_chr_val := v_xml_val::TEXT;
    ELSIF (v_col_data_type = 'BYTEA') THEN
        v_chr_val := upper(encode(v_bin_val, 'hex'));
    END IF;

    p_actual_len := coalesce(char_length(v_chr_val), 0);
    IF (v_col_size != 0 AND v_col_size < p_actual_len)
    THEN
        p_column_err := 1406;
    END IF;

    v_chr_val := CASE
                    WHEN v_col_size IS NULL THEN v_chr_val
                    ELSE substring(v_chr_val, 1, v_col_size)
                 END;

    IF (v_chr_val IS NULL) THEN
        p_column_err := 1405;
    END IF;

    -- Copying value into the OUT argument
    IF (v_out_data_type IN ('TEXT',
                            'CHARACTER',
                            'CHARACTER VARYING'))
    THEN
        v_out_var_size := coalesce(length(p_column_val::BYTEA, pg_client_encoding()), 0);

        IF (v_out_data_type != 'CHARACTER') THEN
            p_column_val := v_chr_val;
        ELSIF (v_out_var_size != 0) THEN
            IF (v_out_var_size < p_actual_len) THEN
                RAISE string_data_length_mismatch;
            END IF;

            p_column_val := rpad(v_chr_val, v_out_var_size, ' ');
        ELSE
            p_column_val := v_chr_val;
        END IF;
    ELSIF (v_out_data_type IN ('REAL',
                               'BIGINT',
                               'INTEGER',
                               'NUMERIC',
                               'DECIMAL',
                               'SMALLINT',
                               'DOUBLE PRECISION'))
    THEN
        p_column_val := trim(v_chr_val)::NUMERIC;
    ELSIF (v_out_data_type IN ('DATE',
                               'TIMESTAMP WITHOUT TIME ZONE',
                               'TIMESTAMP WITH TIME ZONE'))
    THEN
        p_column_val := CASE v_out_data_type
                          WHEN 'DATE' THEN to_date(v_chr_val, 'DD-MON-YY')
                          ELSE to_timestamp(v_chr_val, 'DD-MON-YY HH24:MI:SS.US')
                       END;
    ELSIF (v_out_data_type = 'INTERVAL') THEN
        p_column_val := v_chr_val::INTERVAL;
    ELSIF (v_out_data_type = 'BOOLEAN') THEN
        p_column_val := CASE v_chr_val
                           WHEN 'TRUE' THEN TRUE
                           WHEN 'FALSE' THEN FALSE
                        END;
    ELSIF (v_out_data_type = 'BYTEA') THEN
        p_column_val := decode(v_chr_val, 'hex');
    ELSE
        p_column_val := NULL;
    END IF;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Column position is not declared in SELECT list
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Column position not declared in SELECT list.',
                    DETAIL := 'Supplied column position is NULL or zero. Oracle error ''ORA-01007: column not in select list''.',
                    HINT := format('Make sure that supplied column position was previously declared by ''dbms_sql$define_column'' procedure for cursor_id (%s).', p_cursor_id);
    -- Variable was declared using "dbms_sql$define_column"
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared column type, cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable.',
                    HINT := format('Try to use ''dbms_sql$column_value'' procedure to get column (%s) value for cursor_id (%s).',
                                   p_column_pos, p_cursor_id);
    -- Length of the supplied OUT argument is too small
    WHEN string_data_length_mismatch THEN
        RAISE USING MESSAGE := 'Character string buffer too small. Oracle error ''ORA-06502''.',
                    DETAIL := 'Length of the supplied OUT argument is too small to store column value.',
                    HINT := format('Supply OUT argument of proper length, minimum %s bytes to store cursor column value.',
                                   p_actual_len);
    -- Possible data type conversion error
    WHEN invalid_datetime_format THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to datetime).',
                    DETAIL := format('Value %s is not correct for conversion to datetime. Cursor id (%s), column (%s).',
                                     quote_literal(v_chr_val), p_cursor_id, p_column_pos);
    -- Possible data type conversion error
    WHEN invalid_xml_content THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to XML document).',
                    DETAIL := format('Value %s is not correct for conversion to XML document. Cursor id (%s), column (%s).',
                                     quote_literal(v_chr_val), p_cursor_id, p_column_pos);
    -- Possible data type conversion error
    WHEN invalid_text_representation THEN
        RAISE USING MESSAGE := 'Error during column value conversion (from char to numeric).',
                    DETAIL := format('Value %s is not correct for conversion to numeric. Cursor id (%s), column (%s).',
                                     quote_literal(v_chr_val), p_cursor_id, p_column_pos);
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcol_data JSONB;
    v_chr_val TEXT;
    v_chr_binval BYTEA;
    v_col_base_type VARCHAR;
    v_out_base_type VARCHAR := 'TEXT';
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Length of the segment to retrieve (in bytes) should be 1 or above
    ELSIF (coalesce(p_byte_len, 0) = 0) THEN
        RAISE zero_length_character_string;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Getting dynamic SQL statement defined columns info (JSON)
    v_defcol_data := (v_cur_metadata #> concat('{defcols_data, ', 'col_', p_column_pos, '}')::TEXT[])::JSONB;

    v_col_base_type := (v_defcol_data ->> 'base_type')::VARCHAR; -- Column base data type

    -- Check if data is previously fetched from cursor
    IF ((v_cur_metadata ->> 'curr_row') IS NULL) THEN
        RAISE null_value_not_allowed;
    -- Column position should be declared in SELECT list
    ELSIF (v_col_base_type IS NULL) THEN
        RAISE too_many_columns;
    -- Check if OUT argument base type matches declared column base type
    ELSIF (v_out_base_type != substring(v_col_base_type, 1, 14)) THEN
        RAISE datatype_mismatch;
    -- Column should not be declared using "dbms_sql$define_column_char"
    ELSIF ((v_defcol_data ->> 'is_fixed')::SMALLINT = 1) THEN
        RAISE most_specific_type_mismatch;
    END IF;

    /* Extracting row data from execution results
       table "dbms_sql$recordset"
       (by cursor id, row number, column position)
    */
    SELECT chr_val
      INTO v_chr_val
      FROM dbms_sql$recordset
     WHERE cursor_id = p_cursor_id
       AND rownum = (v_cur_metadata ->> 'curr_row')::INTEGER
       AND column_pos = p_column_pos;

    -- Extracting data from binary string and copying it into the OUT argument
    v_chr_binval := substring(v_chr_val::BYTEA from (p_offset + 1) for p_byte_len);
    p_column_val := convert_from(v_chr_binval, pg_client_encoding());
    p_length_val := coalesce(octet_length(v_chr_binval), 0)::NUMERIC;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Length of the segment to retrieve (in bytes) is zero
    WHEN zero_length_character_string THEN
        RAISE USING MESSAGE := format('Cannot retrieve a zero-length data from resulting column (pos. %s).', p_column_pos),
                    DETAIL := 'Length of the segment to retrieve (in bytes) should be 1 or above',
                    HINT := 'Change the value of byte length parameter (p_byte_len) to 1 or above.';
    -- Resulting data wasn't previously fetched from cursor
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := format('The value of column (%s), cursor_id (%s) cannot be retrieved.', p_column_pos, p_cursor_id),
                    DETAIL := format('Resulting data wasn''t previously fetched for cursor_id (%s). Oracle error ''ORA-01016: This function can be called only after a fetch''.',
                                     p_cursor_id),
                    HINT := format('Use ''dbms_sql$fetch'' or ''dbms_sql$execute_and_fetch'' functions to perform fetch operation for cursor_id (%s).', p_cursor_id);
    -- Column position is not declared in SELECT list
    WHEN too_many_columns THEN
        RAISE USING MESSAGE := 'Column position not declared in SELECT list.',
                    DETAIL := 'Supplied column position is NULL or zero. Oracle error ''ORA-01007: column not in select list''.',
                    HINT := format('Make sure that supplied column position was previously declared by ''dbms_sql$define_column'' procedure for cursor_id (%s).', p_cursor_id);
    -- OUT argument base type doesn't match declared column base type
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared type of column (%s), cursor_id (%s).', p_column_pos, p_cursor_id),
                    DETAIL := format('OUT argument type %s, column type %s. Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable''.',
                                     v_out_base_type, v_col_base_type),
                    HINT := format('Make sure that supplied out argument type match that for cursor_id (%s) column (%s).', p_cursor_id, p_column_pos);
    -- Column was declared using "dbms_sql$define_column_char"
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared variable type, cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable',
                    HINT := format('Try to use ''dbms_sql$column_value_char'' procedure to get column (%s) value for cursor_id (%s).',
                                   p_column_pos, p_cursor_id);
END;

DECLARE
    v_cur_metadata JSONB;
    v_defcols_data JSONB;
    v_is_fixed SMALLINT;
    v_col_size INTEGER;
    v_colvar_len INTEGER;
    v_col_base_type VARCHAR;
    v_col_data_type VARCHAR := upper(pg_typeof(p_column_var)::VARCHAR);
BEGIN
    /* Requires next functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable;
       - set_package_variable.
    */

    -- Exit from procedure if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Column position should be within the range 1..cols_count
    IF (p_column_pos NOT BETWEEN 1 AND (v_cur_metadata ->> 'cols_count')::SMALLINT) THEN
        RAISE check_violation;
    END IF;

    IF (v_col_data_type IN ('TEXT',
                            'CHARACTER',
                            'CHARACTER VARYING'))
    THEN
        v_col_base_type := 'TEXT';
        v_colvar_len := coalesce(length(p_column_var::BYTEA, pg_client_encoding()), 0);
        v_col_size := CASE
                         WHEN p_col_size IS NOT NULL THEN p_col_size
                         WHEN v_colvar_len > 0 THEN v_colvar_len
                      END;
    ELSIF (v_col_data_type IN ('REAL',
                               'BIGINT',
                               'INTEGER',
                               'NUMERIC',
                               'DECIMAL',
                               'SMALLINT',
                               'DOUBLE PRECISION'))
    THEN
        v_col_base_type := 'NUMERIC';
    ELSIF (v_col_data_type = 'MONEY') THEN
        v_col_base_type := 'MONEY';
    ELSIF (v_col_data_type IN ('DATE',
                               'TIMESTAMP WITHOUT TIME ZONE'))
    THEN
        v_col_base_type := 'TIMESTAMP WITHOUT TIME ZONE';
    ELSIF (v_col_data_type = 'TIMESTAMP WITH TIME ZONE') THEN
        v_col_base_type := 'TIMESTAMP WITH TIME ZONE';
    ELSIF (v_col_data_type = 'INTERVAL') THEN
        v_col_base_type := 'INTERVAL';
    ELSIF (v_col_data_type = 'BOOLEAN') THEN
        v_col_base_type := 'BOOLEAN';
    ELSIF (v_col_data_type = 'XML') THEN
        v_col_base_type := 'XML';
    ELSIF (v_col_data_type = 'BYTEA') THEN
        v_col_base_type := 'BYTEA';
        v_colvar_len := coalesce(octet_length(p_column_var), 0);
        v_col_size := CASE
                         WHEN p_col_size IS NOT NULL THEN p_col_size
                         WHEN v_colvar_len > 0 THEN v_colvar_len
                      END;
    ELSE RETURN;
    END IF;

    -- Saving defined columns data into temp table
    INSERT INTO dbms_sql$define_column (cursor_id,
                                        column_pos,
                                        data_type,
                                        base_type,
                                        col_size,
                                        is_fixed)
         VALUES (p_cursor_id,
                 p_column_pos,
                 v_col_data_type,
                 v_col_base_type,
                 v_col_size,
                 v_is_fixed)
    ON CONFLICT (cursor_id, column_pos)
    DO UPDATE SET data_type = v_col_data_type,
                  base_type = v_col_base_type,
                  col_size = v_col_size,
                  is_fixed = v_is_fixed;

    -- Serializing defined columns data into JSON object
    SELECT jsonb_object_agg(concat('col_', defcol_data.column_pos), defcol_data.column_data)
      INTO v_defcols_data
      FROM (SELECT defcol3.column_pos,
                   (SELECT to_jsonb(defcol2.*)
                      FROM (SELECT defcol1.data_type,
                                   defcol1.base_type,
                                   defcol1.col_size,
                                   defcol1.is_fixed
                              FROM dbms_sql$define_column AS defcol1
                             WHERE defcol1.cursor_id = defcol3.cursor_id
                               AND defcol1.column_pos = defcol3.column_pos
                           ) AS defcol2
                   ) AS column_data
              FROM dbms_sql$define_column AS defcol3
             WHERE defcol3.cursor_id = p_cursor_id
           ) AS defcol_data;

    -- Setting "defcols_data" (JSON) cursor property
    v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_data}', v_defcols_data);

    -- Saving cursor metadata (JSON)
    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', -- schema name
                                                'dbms_sql', -- package name
                                                concat('cursor_id_', p_cursor_id, '_metadata'), -- context "cursor_id_xxx"
                                                v_cur_metadata); -- cursor metadata JSON
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Column position lies outside the range 1..cols_count
    WHEN check_violation THEN
        RAISE USING MESSAGE := format('Column (%s) not in select list. Oracle error ''ORA-01007''.', p_column_pos),
                    HINT := format('Check that SQL statement, associated with cursor_id (%s), contains mentioned number of columns.', p_cursor_id);
END;

DECLARE v_cur_metadata JSONB; v_defcols_data JSONB; v_is_fixed SMALLINT := 1; v_col_base_type VARCHAR := 'TEXT'; BEGIN /* Requires next functions to be installed in "aws_oracle_ext" schema: - dbms_sql$is_open; - get_package_variable; - set_package_variable. */
-- Exit from procedure if the cursor (cursor_id) is not opened IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; END IF;
-- Getting cursor metadata (JSON) v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name 'dbms_sql', -- package name concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"
-- Column position should be within the range 1..cols_count IF (p_column_pos NOT BETWEEN 1 AND (v_cur_metadata ->> 'cols_count')::SMALLINT) THEN RAISE check_violation; END IF;
-- Saving defined columns data into temp table INSERT INTO dbms_sql$define_column (cursor_id, column_pos, data_type, base_type, col_size, is_fixed) VALUES (p_cursor_id, p_column_pos, v_col_base_type, v_col_base_type, p_col_size, v_is_fixed) ON CONFLICT (cursor_id, column_pos) DO UPDATE SET data_type = v_col_base_type, base_type = v_col_base_type, col_size = p_col_size, is_fixed = v_is_fixed;
-- Serializing defined columns data into JSON object SELECT jsonb_object_agg(concat('col_', defcol_data.column_pos), defcol_data.column_data) INTO v_defcols_data FROM (SELECT defcol3.column_pos, (SELECT to_jsonb(defcol2.*) FROM (SELECT defcol1.data_type, defcol1.base_type, defcol1.col_size, defcol1.is_fixed FROM dbms_sql$define_column AS defcol1 WHERE defcol1.cursor_id = defcol3.cursor_id AND defcol1.column_pos = defcol3.column_pos ) AS defcol2 ) AS column_data FROM dbms_sql$define_column AS defcol3 WHERE defcol3.cursor_id = p_cursor_id ) AS defcol_data;
-- Setting "defcols_data" (JSON) cursor property v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_data}', v_defcols_data);
-- Saving cursor metadata (JSON) PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', -- schema name 'dbms_sql', -- package name concat('cursor_id_', p_cursor_id, '_metadata'), -- context "cursor_id_xxx" v_cur_metadata); -- cursor metadata JSON EXCEPTION -- Cursor isn't opened or doesn't exist WHEN invalid_cursor_state THEN RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id), DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id), HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id); -- Column position lies outside the range 1..cols_count WHEN check_violation THEN RAISE USING MESSAGE := format('Column (%s) not in select list. Oracle error ''ORA-01007''.', p_column_pos), HINT := format('Check that SQL statement, associated with cursor_id (%s), contains mentioned number of columns.', p_cursor_id); END;

BEGIN
    /* Requires next functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$define_column.
    */

    PERFORM aws_oracle_ext.dbms_sql$define_column(p_cursor_id,
                                                  p_column_pos,
                                                  ''::TEXT);
END;

DECLARE v_cur_metadata JSONB; v_curcols_data JSONB; v_colscount_block TEXT; v_colstype_block TEXT; v_prepdynsql_block TEXT; v_sql_statement TEXT; v_selectsql_stat TEXT; v_selectsql_param TEXT; v_cols_count SMALLINT; v_cases_list TEXT; v_rows_count INTEGER; v_vars_sync_sql TEXT; v_col_aliases VARCHAR; v_coldtype_cases VARCHAR; v_into_clause VARCHAR; v_bind_var RECORD; v_bindvar_val TEXT; v_bindvar_col VARCHAR; v_bind_varstr VARCHAR; v_datatypes_rec RECORD; v_varlabels RECORD; v_err_message TEXT; v_err_stack TEXT; v_defcols_hash VARCHAR; v_param_list VARCHAR; v_param_list_arr VARCHAR[]; v_sql_function_code SMALLINT; v_param_counter SMALLINT := 0; v_debug_mode BOOLEAN := 0; BEGIN v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', p_cursor_id));
IF (v_cur_metadata ->> 'from_refcursor') THEN RAISE invalid_cursor_definition; ELSIF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; END IF;
SELECT sql_statement, sql_statement, sql_statement INTO v_sql_statement, v_selectsql_stat, v_selectsql_param FROM dbms_sql$cursor WHERE cursor_id = p_cursor_id;
IF (v_sql_statement IS NULL) THEN RAISE null_value_not_allowed; END IF;
v_sql_function_code := aws_oracle_ext.dbms_sql$last_sql_function_code(p_cursor_id)::SMALLINT;
IF (v_sql_statement ~* '^SELECT(.)*FROM(.)*INTO\s*(\$\d+(,\s*)?)+\s*$' AND v_sql_function_code NOT IN (3, 5, 9)) THEN v_into_clause := aws_oracle_ext.regexp_substr(v_sql_statement, '(\s*INTO\s*(\$\d+(,\s*)?)+)\s*$', 1, 1, 'i');
v_sql_statement := replace(v_sql_statement, v_into_clause, '');
v_into_clause := regexp_replace(v_into_clause, 'INTO', 'INTO STRICT', 'gi');
v_sql_statement := overlay(v_sql_statement placing concat(' ', ltrim(v_into_clause)) from position('FROM' in upper(v_sql_statement)) - 2 for 1); ELSIF (v_sql_function_code IN (3, 5, 9) AND v_sql_statement ~* 'RETURNING') THEN v_sql_statement := regexp_replace(v_sql_statement, '(?:;)\s*INTO', ' INTO', 'gi'); END IF;
FOR v_bind_var IN (SELECT * FROM dbms_sql$bind_variable WHERE cursor_id = p_cursor_id ORDER BY var_pos) LOOP IF (v_bind_var.data_type IN ('TEXT', 'CHARACTER', 'CHARACTER VARYING')) THEN v_bindvar_col := 'chr_val'; v_bindvar_val := CASE WHEN v_bind_var.chr_val IS NOT NULL THEN quote_literal(CASE v_bind_var.data_type WHEN 'CHARACTER' THEN rpad(v_bind_var.chr_val, v_bind_var.value_size, ' ') ELSE substring(v_bind_var.chr_val, 1, v_bind_var.value_size) END) ELSE NULL END; ELSIF (v_bind_var.data_type IN ('REAL', 'BIGINT', 'INTEGER', 'NUMERIC', 'DECIMAL', 'SMALLINT', 'DOUBLE PRECISION')) THEN v_bindvar_col := 'num_val'; v_bindvar_val := CASE WHEN v_bind_var.num_val IS NOT NULL THEN concat(v_bind_var.num_val::TEXT, '::', CASE v_bind_var.data_type WHEN 'DECIMAL' THEN 'NUMERIC' ELSE v_bind_var.data_type END) ELSE NULL END; ELSIF (v_bind_var.data_type = 'MONEY') THEN v_bindvar_col := 'money_val'; v_bindvar_val := CASE WHEN v_bind_var.money_val IS NOT NULL THEN quote_literal(v_bind_var.money_val::TEXT) ELSE NULL END; ELSIF (v_bind_var.data_type IN ('DATE', 'TIMESTAMP WITHOUT TIME ZONE')) THEN v_bindvar_col := 'tstamp_val'; v_bindvar_val := CASE WHEN v_bind_var.tstamp_val IS NOT NULL THEN CASE v_bind_var.data_type WHEN 'DATE' THEN format('to_date(''%s'', ''DD.MM.YYYY'')', to_char(v_bind_var.tstamp_val, 'DD.MM.YYYY')) ELSE format('to_timestamp(''%s'', ''DD.MM.YYYY HH24:MI:SS.MS.US'')::TIMESTAMP WITHOUT TIME ZONE', to_char(v_bind_var.tstamp_val, 'DD.MM.YYYY HH24:MI:SS.MS.US')) END ELSE NULL END; ELSIF (v_bind_var.data_type = 'TIMESTAMP WITH TIME ZONE') THEN v_bindvar_col := 'tstamptz_val'; v_bindvar_val := CASE WHEN v_bind_var.tstamptz_val IS NOT NULL THEN format('to_timestamp(''%s'', ''DD.MM.YYYY HH24:MI:SS.MS.US'')', to_char(v_bind_var.tstamptz_val, 'DD.MM.YYYY HH24:MI:SS.MS.US')) ELSE NULL END; ELSIF (v_bind_var.data_type = 'INTERVAL') THEN v_bindvar_col := 'interval_val'; v_bindvar_val := CASE WHEN v_bind_var.interval_val IS NOT NULL THEN quote_literal(v_bind_var.interval_val::TEXT) ELSE NULL END; ELSIF (v_bind_var.data_type = 'BOOLEAN') THEN v_bindvar_col := 'bool_val'; v_bindvar_val := CASE WHEN v_bind_var.bool_val IS NOT NULL THEN upper(v_bind_var.bool_val::TEXT) ELSE NULL END; ELSIF (v_bind_var.data_type = 'XML') THEN v_bindvar_col := 'xml_val'; v_bindvar_val := CASE WHEN v_bind_var.xml_val IS NOT NULL THEN quote_literal(v_bind_var.xml_val) ELSE NULL END; ELSIF (v_bind_var.data_type = 'BYTEA') THEN v_bindvar_col := 'bin_val'; v_bindvar_val := CASE WHEN v_bind_var.bin_val IS NOT NULL THEN format('decode(%s, ''base64'')', quote_literal(encode(v_bind_var.bin_val, 'base64'))) ELSE NULL END; ELSE CONTINUE; END IF;
v_bind_varstr := concat_ws(chr(10), v_bind_varstr, format(' v_%s %s%s', v_bind_var.var_name, CASE v_bind_var.data_type WHEN 'CHARACTER' THEN 'CHARACTER VARYING' ELSE v_bind_var.data_type END, CASE WHEN v_bindvar_val IS NULL THEN ';' ELSE format(' := %s;', v_bindvar_val) END));
v_sql_statement := regexp_replace(v_sql_statement, concat(':', v_bind_var.var_name), concat('outer_block.v_', v_bind_var.var_name), 'gi');
FOR v_varlabels IN (SELECT unnest(regexp_matches(v_bind_var.var_label, '(\$\d+)', 'gi')) AS marker) LOOP v_sql_statement := regexp_replace(v_sql_statement, replace(v_varlabels.marker, '$', '\$'), concat('outer_block.v_', v_bind_var.var_name), 'gi'); END LOOP;
IF (v_bind_var.var_label IS NULL) THEN v_param_counter := v_param_counter + 1; v_param_list_arr := array_append(v_param_list_arr, concat('outer_block.v_', v_bind_var.var_name)::VARCHAR); v_selectsql_param := regexp_replace(v_selectsql_param, concat(':', v_bind_var.var_name), concat('$', v_param_counter), 'gi'); END IF;
v_vars_sync_sql := concat_ws(chr(10), v_vars_sync_sql, '', format('-- Updating "%s" bind variable value', v_bind_var.var_name), 'UPDATE dbms_sql$bind_variable', format(' SET %s = outer_block.v_%s', v_bindvar_col, v_bind_var.var_name), ' WHERE cursor_id = outer_block.v_cursor_id', format(' AND var_name = %s;', quote_literal(v_bind_var.var_name))); END LOOP;
v_cols_count := (v_cur_metadata ->> 'cols_count')::INTEGER;
IF (v_cols_count IS NULL AND (v_cur_metadata ->> 'colinfo_refresh')::SMALLINT = 1) THEN IF (v_sql_function_code IN (4, 27)) THEN IF (coalesce(array_length(v_param_list_arr, 1), 0) != 0) THEN v_param_list := array_to_string(v_param_list_arr, concat_ws(chr(10), ',', ' ')); ELSE SELECT string_agg(concat('outer_block.v_', var_name), concat_ws(chr(10), ',', ' ') ORDER BY ltrim(mrk.marker, '$')::INTEGER) INTO v_param_list FROM (SELECT unnest(regexp_matches(var_label, '(\$\d+)', 'gi')) marker, var_name FROM dbms_sql$bind_variable WHERE cursor_id = p_cursor_id) mrk; END IF;
v_colscount_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_dynsql_row RECORD;', ' v_cols_count SMALLINT;', format('v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, 'BEGIN', 'EXECUTE', quote_literal(concat_ws(chr(10), 'SELECT src.* FROM (', '------------------- Dynamic SQL statement -------------------', v_selectsql_param, '------------------- Dynamic SQL statement -------------------', ') AS src LIMIT 0')), concat('INTO outer_block.v_dynsql_row', CASE WHEN v_param_list IS NULL THEN ';' ELSE concat(chr(10), 'USING ', v_param_list, ';') END), '', 'outer_block.v_cols_count := -- Calculating dymamic sql statement columns count', ' aws_oracle_ext.regexp_count(regexp_replace(outer_block.v_dynsql_row::TEXT, ''"(.*?)"'', ''x'', ''gi''), '','') + 1;', '', 'outer_block.v_cur_metadata := -- Getting cursor metadata (JSON)', ' aws_oracle_ext.get_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', 'format(''cursor_id_%s_metadata'', outer_block.v_cursor_id)); -- context "cursor_id_xxx"', '', 'outer_block.v_cur_metadata := -- Setting "cols_count" (JSON) cursor property', ' jsonb_set(outer_block.v_cur_metadata, ''{cols_count}'', to_jsonb(outer_block.v_cols_count));', '', '-- Saving cursor metadata (JSON)', 'PERFORM aws_oracle_ext.set_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', ' format(''cursor_id_%s_metadata'', outer_block.v_cursor_id), -- context "cursor_id_xxx"', ' outer_block.v_cur_metadata); -- cursor metadata JSON', 'END; $dbms_sql$', 'LANGUAGE plpgsql;');
EXECUTE v_colscount_block;
v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', p_cursor_id));
v_cols_count := (v_cur_metadata ->> 'cols_count')::INTEGER; ELSE v_cols_count := 0; END IF; END IF;
IF (v_cols_count > 0) THEN SELECT string_agg(concat(repeat(' ', 10), 'col_', colnum), concat(',', chr(10))) INTO v_col_aliases FROM generate_series(1, v_cols_count) AS colnum;
IF ((v_cur_metadata ->> 'colinfo_refresh')::SMALLINT = 1) THEN v_colstype_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, 'BEGIN', '-- Deleting previous dynamic sql execution results', 'DELETE FROM dbms_sql$recordset', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '-- Deleting previous dynamic sql columns info', 'DELETE FROM dbms_sql$cursor_column', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '-- Creating temp table in order to reflect the columns structure', 'BEGIN', format('CREATE TEMP TABLE tbl_curid%s_colstype', p_cursor_id), 'WITH (OIDS = FALSE)', 'ON COMMIT DROP', 'AS', 'SELECT src.* FROM (', '------------------- Dynamic SQL statement -------------------', v_sql_statement, '------------------- Dynamic SQL statement -------------------', ') AS src', 'WITH NO DATA;', '', '/* Using column aliases instead of names', ' in case if any duplicate columns found */', 'EXCEPTION', 'WHEN duplicate_column THEN', '', format('CREATE TEMP TABLE tbl_curid%s_colstype', p_cursor_id), 'WITH (OIDS = FALSE)', 'ON COMMIT DROP', 'AS', 'SELECT src.* FROM (', '------------------- Dynamic SQL statement -------------------', v_sql_statement, '------------------- Dynamic SQL statement -------------------', format(') AS src (%s)', trim(v_col_aliases)), 'WITH NO DATA;', 'END;', '', concat_ws(chr(10), '-- Saving detailed column data (name, type, precision, etc.)', 'INSERT INTO dbms_sql$cursor_column (cursor_id,', ' column_pos,', ' col_name,', ' data_type,', ' base_type,', ' is_nullable,', ' chr_max_len,', ' chr_byte_len,', ' num_precision,', ' num_scale,', ' tstamp_precision,', ' interval_type,', ' interval_precision)', 'SELECT outer_block.v_cursor_id,', ' ordinal_position,', ' column_name,', ' upper(data_type) AS data_type,', ' CASE', ' WHEN data_type IN (''text'',', ' ''character'',', ' ''character varying'') THEN ''TEXT''', ' WHEN data_type IN (''real'',', ' ''bigint'',', ' ''integer'',', ' ''numeric'',', ' ''decimal'',', ' ''smallint'',', ' ''double precision'') THEN ''NUMERIC''', ' WHEN data_type IN (''date'',', ' ''timestamp without time zone'') THEN ''TIMESTAMP WITHOUT TIME ZONE''', ' ELSE upper(data_type)', ' END AS base_type,', ' CASE is_nullable', ' WHEN ''YES'' THEN TRUE', ' ELSE FALSE', ' END AS is_nullable,', ' character_maximum_length,', ' character_octet_length,', ' numeric_precision,', ' numeric_scale,', ' datetime_precision,', ' interval_type,', ' interval_precision', ' FROM information_schema.columns'), format(' WHERE table_name = ''tbl_curid%s_colstype''', p_cursor_id), ' ORDER BY ordinal_position ASC;', '', '-- Dropping temp table since its not needed anymore', format('DROP TABLE IF EXISTS tbl_curid%s_colstype;', p_cursor_id), 'END; $dbms_sql$', 'LANGUAGE plpgsql;');
EXECUTE v_colstype_block;
SELECT jsonb_object_agg(concat('col_', curcol_data.column_pos), curcol_data.column_data) INTO v_curcols_data FROM (SELECT curcol3.column_pos, (SELECT to_jsonb(curcol2.*) FROM (SELECT curcol1.data_type, curcol1.base_type FROM dbms_sql$cursor_column AS curcol1 WHERE curcol1.cursor_id = curcol3.cursor_id AND curcol1.column_pos = curcol3.column_pos ) AS curcol2 ) AS column_data FROM dbms_sql$cursor_column AS curcol3 WHERE curcol3.cursor_id = p_cursor_id ) AS curcol_data;
v_cur_metadata := jsonb_set(v_cur_metadata, '{curcols_data}', v_curcols_data);
v_cur_metadata := jsonb_set(v_cur_metadata, '{colinfo_refresh}', to_jsonb(0)); END IF;
v_defcols_hash := md5(v_cur_metadata ->> 'defcols_data');
IF (v_defcols_hash IS NOT NULL AND v_defcols_hash != coalesce(v_cur_metadata ->> 'defcols_hash', '')) THEN v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_hash}', to_jsonb(v_defcols_hash));
FOR v_datatypes_rec IN (SELECT curcol.column_pos, curcol.data_type AS cur_data_type, defcol.data_type AS def_data_type FROM dbms_sql$cursor_column AS curcol INNER JOIN dbms_sql$define_column AS defcol ON (defcol.cursor_id = curcol.cursor_id AND defcol.column_pos = curcol.column_pos AND defcol.base_type != curcol.base_type AND coalesce(defcol.is_fixed, 0) != 1) WHERE curcol.cursor_id = p_cursor_id) LOOP CONTINUE WHEN substring(v_datatypes_rec.cur_data_type, 1, 14) = substring(v_datatypes_rec.def_data_type, 1, 14);
RAISE datatype_mismatch; END LOOP; END IF;
SELECT string_agg(coalesce(concat(' CASE colpos', chr(10)) || (SELECT string_agg(format(' WHEN %s THEN col_%s', colpos, colpos), chr(10)) FROM unnest(colpos_list) AS colpos) || concat(chr(10), ' END'), ' NULL'), concat(',', chr(10)) ORDER BY datatypes.dtype_order ASC) INTO v_coldtype_cases FROM (SELECT 'TEXT' AS base_type, 1 AS dtype_order UNION ALL SELECT 'NUMERIC', 2 UNION ALL SELECT 'MONEY', 3 UNION ALL SELECT 'TIMESTAMP WITHOUT TIME ZONE', 4 UNION ALL SELECT 'TIMESTAMP WITH TIME ZONE', 5 UNION ALL SELECT 'INTERVAL', 6 UNION ALL SELECT 'BOOLEAN', 7 UNION ALL SELECT 'XML', 8 UNION ALL SELECT 'BYTEA', 9) AS datatypes LEFT JOIN (SELECT base_type, array_agg(column_pos) AS colpos_list FROM dbms_sql$cursor_column WHERE cursor_id = p_cursor_id GROUP BY base_type) AS curcols ON (curcols.base_type = datatypes.base_type);
v_sql_statement := concat_ws(chr(10), '-- Deleting previous dynamic sql execution resulting data', 'DELETE FROM dbms_sql$recordset', ' WHERE cursor_id = outer_block.v_cursor_id;', '', '/* Execution of dynamic SQL statement and inserting it''s', ' resulting data into temp table dbms_sql$recordset */', 'INSERT INTO dbms_sql$recordset (cursor_id,', ' rownum,', ' column_pos,', ' chr_val,', ' num_val,', ' money_val,', ' tstamp_val,', ' tstamptz_val,', ' interval_val,', ' bool_val,', ' xml_val,', ' bin_val)', 'SELECT outer_block.v_cursor_id,', ' rownum,', ' colpos,', v_coldtype_cases, ' FROM (SELECT row_number() over (order by 1) AS rownum,', ' generate_series(1, outer_block.v_cols_count) AS colpos,', ' dynsql.*', ' FROM (', '------------------- Dynamic SQL statement -------------------', v_sql_statement, '------------------- Dynamic SQL statement -------------------', concat(' ) AS dynsql (', v_col_aliases, ')'), ' ) AS dynsql2;'); END IF;
IF (v_sql_function_code = 34) THEN v_sql_statement := regexp_replace(v_sql_statement, '(^\s*DO(:?\s+LANGUAGE\s+[[:alpha:]]+\s+)?\s*\$(\w*)?\$\s*(:?<<.+>>)?\s*)', concat(substring(v_sql_statement, '<<.+>>'), chr(10)), 'gi');
v_sql_statement := regexp_replace(v_sql_statement, '(END\s*(;)?\s*\$(\w*)?\$\s*(:?\s+LANGUAGE\s+[[:alpha:]]+)?\s*(;)?$)', 'END;', 'gi');
v_sql_statement := regexp_replace(v_sql_statement, '^\s*(?=\S*)|(?<=\S*)\s*$', '', 'gi'); END IF;
PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', p_cursor_id), v_cur_metadata);
v_prepdynsql_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_rows_count INTEGER;', format(' v_cols_count SMALLINT := %s;', v_cols_count), format(' v_cursor_id INTEGER := %s;', p_cursor_id), v_bind_varstr, 'BEGIN', '---- Dynamic PL/pgSQL statement (or dynamic SQL wrapper) ----', CASE WHEN right(v_sql_statement, 1) != ';' THEN concat(v_sql_statement, ';') ELSE v_sql_statement END, '---- Dynamic PL/pgSQL statement (or dynamic SQL wrapper) ----', '', 'GET DIAGNOSTICS outer_block.v_rows_count := ROW_COUNT;', '', 'outer_block.v_cur_metadata := -- Getting cursor metadata (JSON)', ' aws_oracle_ext.get_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', ' format(''cursor_id_%s_metadata'', outer_block.v_cursor_id)); -- context "cursor_id_xxx"', '', '-- Setting "rows_count" (JSON) cursor property', 'outer_block.v_cur_metadata :=', ' jsonb_set(outer_block.v_cur_metadata,', ' ''{rows_count}'',', ' CASE outer_block.v_cols_count', ' WHEN 0 THEN to_jsonb(outer_block.v_rows_count)', ' ELSE to_jsonb(outer_block.v_rows_count / outer_block.v_cols_count)', ' END);', '', '-- Setting "curr_row" (JSON) cursor property', 'outer_block.v_cur_metadata := jsonb_set(outer_block.v_cur_metadata, ''{curr_row}'', JSONB ''null'');', '', '-- Saving cursor metadata (JSON)', 'PERFORM aws_oracle_ext.set_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', ' format(''cursor_id_%s_metadata'', outer_block.v_cursor_id), -- context "cursor_id_xxx"', ' outer_block.v_cur_metadata); -- cursor metadata JSON', v_vars_sync_sql, 'END; $dbms_sql$', 'LANGUAGE plpgsql;');
EXECUTE v_prepdynsql_block;
IF v_debug_mode THEN UPDATE dbms_sql$cursor SET colscount_block = coalesce(v_colscount_block, colscount_block), colstype_block = coalesce(v_colstype_block, colstype_block), prepdynsql_block = v_prepdynsql_block WHERE cursor_id = p_cursor_id; END IF;
v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', p_cursor_id)); RETURN (v_cur_metadata ->> 'rows_count')::NUMERIC; EXCEPTION WHEN invalid_cursor_state THEN RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id), DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id), HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id); WHEN invalid_cursor_definition THEN RAISE USING MESSAGE := 'Can not repeatedly execute a REFCURSOR-sourced cursor.', DETAIL := format('Specified cursor (cursor_id %s) is a REFCURSOR-sourced cursor, it can not be executed through ''dbms_sql$execute'' or ''dbms_sql$execute_and_fetch'' procedures.', p_cursor_id), HINT := format('Cursor with cursor_id (%s) was previously executed. There is no need to execute it again.', p_cursor_id); WHEN null_value_not_allowed THEN RAISE USING MESSAGE := format('No SQL statement is found for the supplied cursor_id (%s)', p_cursor_id), DETAIL := format('There is no SQL statement associated with the supplied cursor_id (%s), or such cursor_id (%s) is not exist', p_cursor_id), HINT := format('Make sure that cursor_id (%s) exists, and PARSE procedure with proper SQL statement was successfully performed for it', p_cursor_id); WHEN datatype_mismatch THEN RAISE USING MESSAGE := format('Inconsistent datatypes: expected %s got %s. Oracle error ''ORA-00932''.', v_datatypes_rec.def_data_type, v_datatypes_rec.cur_data_type), DETAIL := format('Data types declared for cursor_id (%s) column (%s) do not match.', p_cursor_id, v_datatypes_rec.column_pos), HINT := 'Try to use ''dbms_sql$define_column'' procedure to declare the proper data type for mentioned column.'; WHEN syntax_error THEN GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT, v_err_stack = PG_EXCEPTION_CONTEXT;
RAISE USING MESSAGE := 'Syntax error has occured while trying to execute dynamic SQL statement.', DETAIL := concat(v_err_message, '.'), HINT := concat_ws(chr(10), '(Call Stack)', v_err_stack); END;

DECLARE
    v_rows_count INTEGER;
    v_rows_fetched INTEGER;
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$execute;
       - dbms_sql$fetch_rows.
    */

    v_rows_count := aws_oracle_ext.dbms_sql$execute(p_cursor_id);
    v_rows_fetched := aws_oracle_ext.dbms_sql$fetch_rows(p_cursor_id);

    /* Raise an exception if the number of rows actually matching the query differs from 1.
       This behaviour is actual if "p_exact_match" param passed value is "TRUE".
       -------------------------------------------------------------
       "ORA-01403: no data found" - if v_rows_count is null or zero.
       "ORA-01422: exact fetch returns more than requested number of rows" - if "v_rows_count" more than one.
    */
    IF (p_exact_match) THEN
        IF (v_rows_count > 1) THEN
            -- ORA-01422
            RAISE too_many_rows;
        ELSIF (coalesce(v_rows_count, 0) = 0) THEN
            -- ORA-01403
            RAISE no_data_found;
        END IF;
    END IF;

    RETURN v_rows_fetched;
EXCEPTION
    -- More than one row fetched from cursor
    WHEN too_many_rows THEN
        RAISE USING MESSAGE := format('Exact fetch from cursor_id (%s) returns more than requested number of rows.', p_cursor_id),
                    DETAIL := 'Execution of dynamic SQL statement results in more than one row. Oracle error ''ORA-01422''.',
                    HINT := format('Make sure that dynamic SQL statement associated with cursor_id (%s) is forced to return exactly one row.', p_cursor_id);
    -- Fetch from cursor results in zero rows
    WHEN no_data_found THEN
        RAISE USING MESSAGE := format('No rows are fetched from cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Execution of dynamic SQL statement results in 0 rows. Oracle error ''ORA-01403: no data found''.',
                      HINT := format('Make sure that dynamic SQL statement associated with cursor_id (%s) exists and return at least one row.', p_cursor_id);
END;

DECLARE
    v_cur_metadata JSONB;
    v_curr_row INTEGER;
    v_rows_count INTEGER;
    v_datatypes_rec RECORD;
    v_defcols_hash VARCHAR;
    v_defcols_prevhash VARCHAR;
    v_rows_fetched INTEGER := 0;
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable;
       - set_package_variable.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Additional check if cursor (cursor_id) was transformed from a REFCURSOR
    IF (v_cur_metadata ->> 'from_refcursor') THEN
        -- Getting defined columns data hash
        v_defcols_hash := md5(v_cur_metadata ->> 'defcols_data');

        IF (v_defcols_hash IS NOT NULL AND
            v_defcols_hash != coalesce(v_cur_metadata ->> 'defcols_hash', ''))
        THEN
            -- Setting "defcols_hash" (JSON) cursor property
            v_cur_metadata := jsonb_set(v_cur_metadata, '{defcols_hash}', to_jsonb(v_defcols_hash));

            /* Checking for correspondence between actual and declared column base data types
               Oracle ORA-00932: INCONSISTENT_TYPE error is raised if the type of the given OUT parameter value is different from the actual type of the value.
               This type was the given type when the column was defined by calling procedure dbms_sql$define_column.
            */
            FOR v_datatypes_rec IN
            (SELECT curcol.column_pos,
                    curcol.data_type AS cur_data_type,
                    defcol.data_type AS def_data_type
               FROM dbms_sql$cursor_column AS curcol
                    INNER JOIN dbms_sql$define_column AS defcol ON (defcol.cursor_id = curcol.cursor_id AND
                                                                    defcol.column_pos = curcol.column_pos AND
                                                                    defcol.base_type != curcol.base_type AND
                                                                    coalesce(defcol.is_fixed, 0) != 1)
              WHERE curcol.cursor_id = p_cursor_id)
            LOOP
                -- Data types TIMESTAMP WITHOUT TIME ZONE and TIMESTAMP WITH TIME ZONE are interconvertible, thus they should not generate an error
                CONTINUE WHEN substring(v_datatypes_rec.cur_data_type, 1, 14) = substring(v_datatypes_rec.def_data_type, 1, 14);

                RAISE datatype_mismatch;
            END LOOP;
        END IF;
    END IF;

    -- Cursor rows count should not be empty (null)
    IF ((v_cur_metadata ->> 'rows_count') IS NULL) THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Perform fetching while there are rows to fetch
    IF (coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0) != (v_cur_metadata ->> 'rows_count')::INTEGER) THEN
        -- Setting "curr_row" (JSON) cursor property
        v_cur_metadata := jsonb_set(v_cur_metadata, '{curr_row}', to_jsonb(coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0) + 1));

        v_rows_fetched := 1;
    END IF;

    -- Saving cursor metadata (JSON)
    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', -- schema name
                                                'dbms_sql', -- package name
                                                concat('cursor_id_', p_cursor_id, '_metadata'), -- context "cursor_id_xxx"
                                                v_cur_metadata); -- cursor metadata JSON
    RETURN v_rows_fetched;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Actual and declared base data types of a column are not match
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := format('Inconsistent datatypes: expected %s got %s. Oracle error ''ORA-00932''.',
                                      v_datatypes_rec.def_data_type,
                                      v_datatypes_rec.cur_data_type),
                    DETAIL := format('Data types declared for cursor_id (%s) column (%s) do not match.',
                                     p_cursor_id,
                                     v_datatypes_rec.column_pos),
                    HINT := concat('Try to use ''dbms_sql$define_column'' procedure to declare the proper data type for mentioned column.');
    -- Cursor rows count should is empty (null)
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Fetch out of sequence. Oracle error ''ORA-01002''.',
                    DETAIL := format('Dynamic SQL statement, associated with cursor_id (%s), was not executed.', p_cursor_id),
                    HINT := format('Perform ''dbms_sql$execute'' or ''dbms_sql$execute_and_fetch'' procedure for cursor_id (%s).', p_cursor_id);
END;

BEGIN -- Creating all necessary temp tables IF (NOT aws_oracle_ext.is_package_initialized('aws_oracle_ext', 'dbms_sql')) THEN -- Temp table for storing cursor data (id, sql statement etc.) CREATE TEMP TABLE IF NOT EXISTS dbms_sql$cursor ( cursor_id SERIAL, sql_statement TEXT, colscount_block TEXT, colstype_block TEXT, prepdynsql_block TEXT, CONSTRAINT pk_cursor PRIMARY KEY (cursor_id) ) WITH (OIDS = FALSE) ON COMMIT PRESERVE ROWS;
-- Temp table for storing defined columns data (position, data type etc.) CREATE TEMP TABLE IF NOT EXISTS dbms_sql$define_column ( cursor_id INTEGER, column_pos INTEGER, data_type VARCHAR, base_type VARCHAR, col_size INTEGER, is_fixed SMALLINT, CONSTRAINT pk_def_column PRIMARY KEY (cursor_id, column_pos), CONSTRAINT fk_defcol_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = FALSE) ON COMMIT PRESERVE ROWS;
-- Temp table for storing actual columns data (position, data type etc.) CREATE TEMP TABLE IF NOT EXISTS dbms_sql$cursor_column ( cursor_id INTEGER, column_pos INTEGER, col_name VARCHAR, data_type VARCHAR, base_type VARCHAR, is_nullable BOOLEAN, chr_max_len INTEGER, chr_byte_len INTEGER, num_precision SMALLINT, num_scale SMALLINT, tstamp_precision SMALLINT, interval_type VARCHAR, interval_precision SMALLINT, CONSTRAINT pk_cur_column PRIMARY KEY (cursor_id, column_pos), CONSTRAINT fk_curcol_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = FALSE) ON COMMIT PRESERVE ROWS;
-- Temp table for storing binded variables data (name, data type, value etc.) CREATE TEMP TABLE IF NOT EXISTS dbms_sql$bind_variable ( cursor_id INTEGER, var_pos INTEGER, var_name VARCHAR, var_label VARCHAR, data_type VARCHAR, base_type VARCHAR, var_size INTEGER, value_size INTEGER, is_fixed SMALLINT, chr_val TEXT, num_val NUMERIC, money_val MONEY, tstamp_val TIMESTAMP WITHOUT TIME ZONE, tstamptz_val TIMESTAMP WITH TIME ZONE, interval_val INTERVAL, bool_val BOOLEAN, xml_val XML, bin_val BYTEA, CONSTRAINT pk_bind_var PRIMARY KEY (cursor_id, var_name), CONSTRAINT fk_bindvar_cur FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE ) WITH (OIDS = FALSE) ON COMMIT PRESERVE ROWS;
-- Temp table for storing dynamic statement execution results (parsed SQL record sets) CREATE TEMP TABLE IF NOT EXISTS dbms_sql$recordset ( cursor_id INTEGER, rownum INTEGER, column_pos INTEGER, chr_val TEXT, num_val NUMERIC, money_val MONEY, tstamp_val TIMESTAMP WITHOUT TIME ZONE, tstamptz_val TIMESTAMP WITH TIME ZONE, interval_val INTERVAL, bool_val BOOLEAN, xml_val XML, bin_val BYTEA, CONSTRAINT pk_record_set PRIMARY KEY (cursor_id, rownum, column_pos), CONSTRAINT fk_recset_cursor FOREIGN KEY (cursor_id) REFERENCES dbms_sql$cursor (cursor_id) ON DELETE CASCADE, CONSTRAINT fk_recset_curcol FOREIGN KEY (cursor_id, column_pos) REFERENCES dbms_sql$cursor_column (cursor_id, column_pos) ) WITH (OIDS = FALSE) ON COMMIT PRESERVE ROWS;
-- Marking package "dbms_sql" as initialized PERFORM aws_oracle_ext.set_package_initialized('aws_oracle_ext', 'dbms_sql'); END IF; END;

DECLARE
    v_cur_metadata JSONB;
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - get_package_variable.
    */

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"
    RETURN v_cur_metadata IS NOT NULL;
END;

DECLARE
    v_cur_metadata JSONB;
    v_cursor_id INTEGER;
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - get_package_variable.
    */

    -- Getting the most recent parsed (and executed) cursor id (cursor_id)
    v_cursor_id := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', 'last_parsed_cursor_id')::INTEGER;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', v_cursor_id, '_metadata')); -- context "cursor_id_xxx"

    -- Getting the current fetched rownum by querying the context
    RETURN coalesce((v_cur_metadata ->> 'curr_row')::NUMERIC, 0);
END;

DECLARE
    v_cursor_id INTEGER;
    v_sql_statement TEXT;
    v_cur_metadata JSONB;
    v_actual_cursor_id INTEGER;
    v_function_code INTEGER := 0;
    v_last_parsed_cur_id INTEGER;
    v_last_opened_cur_id INTEGER;
    v_last_cursor_func_code NUMERIC;
BEGIN
    BEGIN
        v_last_parsed_cur_id := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', 'last_parsed_cursor_id')::INTEGER;
    EXCEPTION
        WHEN OTHERS THEN
        NULL;
    END;

    v_last_opened_cur_id := coalesce(aws_oracle_ext.get_extpack_variable('last_opened_cursor_id')::INTEGER, 0);

    v_actual_cursor_id := greatest(v_last_opened_cur_id, v_last_parsed_cur_id);

    BEGIN
        v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext',
                                                              'dbms_sql',
                                                              format('cursor_id_%s_metadata', v_actual_cursor_id));
    EXCEPTION
        WHEN OTHERS THEN
        NULL;
    END;

    BEGIN
        v_last_cursor_func_code := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', 'last_cursor_func_code')::NUMERIC;
    EXCEPTION
        WHEN OTHERS THEN
        NULL;
    END;

    IF (p_cursor_id IS NULL AND
        v_cur_metadata IS NULL AND
        v_last_cursor_func_code IS NOT NULL)
    THEN
        RETURN v_last_cursor_func_code;
    ELSE
        v_cursor_id := coalesce(p_cursor_id, v_actual_cursor_id);
    END IF;

    IF v_cursor_id IS NULL THEN
        RETURN v_function_code;
    END IF;

    SELECT sql_statement
      INTO v_sql_statement
      FROM dbms_sql$cursor
     WHERE cursor_id = v_cursor_id;

    IF v_sql_statement ~* '^\s*(CREATE\s+(?:(?:GLOBAL\s+)|(?:LOCAL\s+))?TEMPORARY\s+TABLE\s+)|(CREATE\s+(?:(?:GLOBAL\s+)|(?:LOCAL\s+))?TEMP\s+TABLE\s+)|(CREATE\s+(?:UNLOGGED\s+)?TABLE\s+)'
    THEN
        v_function_code := 1;
    ELSIF v_sql_statement ~* '^\s*(SET\s+ROLE)\s+'
    THEN
        v_function_code := 2;
    ELSIF v_sql_statement ~* '^\s*(INSERT\s+INTO)\s+'
    THEN
        v_function_code := 3;
    ELSIF (v_sql_statement ~* '^\s*(SELECT)\s+' AND v_sql_statement !~* '^SELECT(.)*INTO\s*(\$[0-9]+(,\s*)?)+\s*$')
    THEN
        v_function_code := 4;
    ELSIF v_sql_statement ~* '^\s*(UPDATE)\s+'
    THEN
        v_function_code := 5;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+ROLE)\s+'
    THEN
        v_function_code := 6;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+VIEW)\s+'
    THEN
        v_function_code := 7;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+TABLE\s+)'
    THEN
        v_function_code := 8;
    ELSIF v_sql_statement ~* '^\s*(DELETE)\s+'
    THEN
        v_function_code := 9;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:TEMP\s+)?(?:TEMPORARY\s+)?VIEW)\s+'
    THEN
        v_function_code := 10;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+USER)\s+|(DROP\s+SCHEMA)\s+'
    THEN
        v_function_code := 11;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+ROLE)\s+'
    THEN
        v_function_code := 12;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:TEMPORARY\s+)?SEQUENCE)|(CREATE\s+(?:TEMP\s+)?SEQUENCE)\s+'
    THEN
        v_function_code := 13;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+SEQUENCE)\s+'
    THEN
        v_function_code := 14;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+SEQUENCE)\s+'
    THEN
        v_function_code := 16;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+CLUSTER)\s+'
    THEN
        v_function_code := 18;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+USER)\s+|(CREATE\s+SCHEMA)\s+'
    THEN
        v_function_code := 19;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:UNIQUE\s+)?INDEX)|(CREATE\s+(?:BITMAP\s+)?INDEX)\s+'
    THEN
        v_function_code := 20;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+INDEX)\s+'
    THEN
        v_function_code := 21;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+CLUSTER)\s+'
    THEN
        v_function_code := 22;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PROCEDURE)\s+'
    THEN
        v_function_code := 24;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+PROCEDURE)\s+'
    THEN
        v_function_code := 25;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+TABLE\s+)'
    THEN
        v_function_code := 26;
    ELSIF v_sql_statement ~* '^\s*(EXPLAIN)\s+'
    THEN
        v_function_code := 27;
    ELSIF v_sql_statement ~* '^\s*(GRANT)\s+'
    THEN
        v_function_code := 28;
    ELSIF v_sql_statement ~* '^\s*(REVOKE)\s+'
    THEN
        v_function_code := 29;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:PUBLIC\s+)?SYNONYM)\s+'
    THEN
        v_function_code := 30;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+(?:PUBLIC\s+)?SYNONYM)\s+'
    THEN
        v_function_code := 31;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+SYSTEM)\s+'
    THEN
        v_function_code := 32;
    ELSIF v_sql_statement ~* '^\s*(SET\s+TRANSACTION)\s+'
    THEN
        v_function_code := 33;
    ELSIF v_sql_statement ~* '(^\s*(?:<<.+>>)?\s*(BEGIN|DECLARE)\s+)|(^\s*DO(:?\s+LANGUAGE\s+[[:alpha:]]+\s+)?\s*\$([[:alnum:]_]*)?\$\s*(:<<.+>>)?\s*)'
    THEN
        v_function_code := 34;
    ELSIF v_sql_statement ~* '^\s*(LOCK)\s+'
    THEN
        v_function_code := 35;
    ELSIF v_sql_statement ~* '^\s*(RENAME\s+[[:alnum:]_$#]+\s+TO)\s+'
    THEN
        v_function_code := 37;
    ELSIF v_sql_statement ~* '^\s*(COMMENT\s+ON)\s+'
    THEN
        v_function_code := 38;
    ELSIF v_sql_statement ~* '^\s*(AUDIT)\s+'
    THEN
        v_function_code := 39;
    ELSIF v_sql_statement ~* '^\s*(NOAUDIT)\s+'
    THEN
        v_function_code := 40;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+INDEX)\s+'
    THEN
        v_function_code := 41;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+DATABASE)\s+'
    THEN
        v_function_code := 44;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+DATABASE)\s+'
    THEN
        v_function_code := 45;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:PUBLIC\s+)?ROLLBACK\s+SEGMENT)\s+'
    THEN
        v_function_code := 46;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+ROLLBACK\s+SEGMENT)\s+'
    THEN
        v_function_code := 47;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+ROLLBACK\s+SEGMENT)\s+'
    THEN
        v_function_code := 48;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:(?:SMALLFILE\s+)|(?:BIGFILE\s+)|(?:UNDO\s+)|(?:TEMPORARY\s+))?TABLESPACE)\s+'
    THEN
        v_function_code := 49;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+TABLESPACE)\s+'
    THEN
        v_function_code := 50;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+TABLESPACE)\s+'
    THEN
        v_function_code := 51;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+SESSION)\s+'
    THEN
        v_function_code := 52;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+USER)\s+|(ALTER\s+SCHEMA)\s+'
    THEN
        v_function_code := 53;
    ELSIF v_sql_statement ~* '^\s*COMMIT(?:\s+WORK)?\s*$'
    THEN
        v_function_code := 54;
    ELSIF v_sql_statement ~* '^\s*ROLLBACK(?:\s+WORK)?\s*$'
    THEN
        v_function_code := 55;
    ELSIF v_sql_statement ~* '^\s*SAVEPOINT(?:\s+[[:alnum:]_$#]+)?\s*$'
    THEN
        v_function_code := 56;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+CONTROLFILE)\s+'
    THEN
        v_function_code := 57;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?(?:CONSTRAINT\s+)?TRIGGER)\s+'
    THEN
        v_function_code := 59;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+TRIGGER)\s+'
    THEN
        v_function_code := 60;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+TRIGGER)\s+'
    THEN
        v_function_code := 61;
    ELSIF v_sql_statement ~* '^\s*(ANALYZE\s+INDEX)\s+'
    THEN
        v_function_code := 63;
    ELSIF v_sql_statement ~* '^\s*(ANALYZE\s+CLUSTER)\s+'
    THEN
        v_function_code := 64;
    ELSIF v_sql_statement ~* '^\s*(ANALYZE\s+TABLE\s+)|(ANALYZE(?:\s+VERBOSE)?\s+)'
    THEN
        v_function_code := 62;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+PROFILE)\s+'
    THEN
        v_function_code := 65;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+PROFILE)\s+'
    THEN
        v_function_code := 66;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+PROFILE)\s+'
    THEN
        v_function_code := 67;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+PROCEDURE)\s+'
    THEN
        v_function_code := 68;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+RESOURCE\s+COST)\s+'
    THEN
        v_function_code := 70;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+'
    THEN
        v_function_code := 71;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+'
    THEN
        v_function_code := 72;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+(MATERIALIZED\s+VIEW|SNAPSHOT)\s+LOG\s+ON)\s+'
    THEN
        v_function_code := 73;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+'
    THEN
        v_function_code := 74;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+'
    THEN
        v_function_code := 75;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+(MATERIALIZED\s+VIEW|SNAPSHOT))\s+'
    THEN
        v_function_code := 76;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?TYPE)\s+(?!BODY)'
    THEN
        v_function_code := 77;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+TYPE)\s+(?!BODY)'
    THEN
        v_function_code := 78;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+ROLE)\s+'
    THEN
        v_function_code := 79;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+TYPE)\s+' AND v_sql_statement !~* 'BODY')
    THEN
        v_function_code := 80;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?TYPE\s+BODY)\s+'
    THEN
        v_function_code := 81;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+TYPE\s+(?:.)*\s+COMPILE\s+(?:DEBUG\s+)?BODY)'
    THEN
        v_function_code := 82;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+TYPE\s+BODY)\s+'
    THEN
        v_function_code := 83;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+LIBRARY)\s+'
    THEN
        v_function_code := 84;
    ELSIF v_sql_statement ~* '^\s*(TRUNCATE\s+TABLE)\s+'
    THEN
        v_function_code := 85;
    ELSIF v_sql_statement ~* '^\s*(TRUNCATE\s+CLUSTER)\s+'
    THEN
        v_function_code := 86;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+BITMAPFILE)\s+'
    THEN
        v_function_code := 87;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+VIEW)\s+'
    THEN
        v_function_code := 88;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+BITMAPFILE)\s+'
    THEN
        v_function_code := 89;
    ELSIF v_sql_statement ~* '^\s*(SET\s+CONSTRAINTS)|(SET\s+CONSTRAINT)\s+'
    THEN
        v_function_code := 90;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?FUNCTION\s+(?:.)*(RETURN|RETURNS))\s+'
    THEN
        v_function_code := 91;
    ELSIF v_sql_statement ~* '^\s*(ALTER\s+FUNCTION)\s+'
    THEN
        v_function_code := 92;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+FUNCTION)\s+'
    THEN
        v_function_code := 93;
    ELSIF (v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN
        v_function_code := 94;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN
        v_function_code := 95;
    ELSIF (v_sql_statement ~* '^\s*(DROP\s+PACKAGE)\s+' AND v_sql_statement !~* 'BODY')
    THEN
        v_function_code := 96;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?PACKAGE\s+BODY)\s+'
    THEN
        v_function_code := 97;
    ELSIF (v_sql_statement ~* '^\s*(ALTER\s+PACKAGE)\s+' AND v_sql_statement ~* 'BODY')
    THEN
        v_function_code := 98;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+PACKAGE\s+BODY)\s+'
    THEN
        v_function_code := 99;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?DIRECTORY)\s+'
    THEN
        v_function_code := 157;
    ELSIF v_sql_statement ~* '^\s*(DROP\s+DIRECTORY)\s+'
    THEN
        v_function_code := 158;
    ELSIF v_sql_statement ~* '^\s*(CREATE\s+(?:OR\s+REPLACE\s+)?LIBRARY)\s+'
    THEN
        v_function_code := 159;
    END IF;

    IF (p_cursor_id IS NULL)
    THEN
        PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext',
                                                    'dbms_sql',
                                                    'last_cursor_func_code',
                                                    v_function_code);
    END IF;

    RETURN v_function_code;
END;

DECLARE
    v_cursor_id INTEGER;
BEGIN
    PERFORM aws_oracle_ext.dbms_sql$init();

    INSERT INTO dbms_sql$cursor (cursor_id)
         VALUES (DEFAULT)
      RETURNING cursor_id
           INTO v_cursor_id;

    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext',
                                                'dbms_sql',
                                                format('cursor_id_%s_metadata', v_cursor_id),
                                                jsonb_build_object('cursor_id', v_cursor_id,
                                                                   'from_refcursor', FALSE));

    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext',
                                                'dbms_sql',
                                                'last_cursor_func_code',
                                                NULL::TEXT);

    PERFORM aws_oracle_ext.set_extpack_variable('last_opened_cursor_id', v_cursor_id);

    RETURN v_cursor_id;
END;

DECLARE
    v_cur_metadata JSONB;
    v_sql_statement TEXT := trim(p_sql_statement);
    v_statement_hash TEXT := coalesce(md5(v_sql_statement), '');
BEGIN
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    ELSIF (coalesce(char_length(v_sql_statement), 0) = 0) THEN
        RAISE invalid_prepared_statement_definition;
    END IF;

    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext',
                                                          'dbms_sql',
                                                          format('cursor_id_%s_metadata', p_cursor_id));

    v_cur_metadata := jsonb_set(v_cur_metadata, '{cols_count}', coalesce(to_jsonb(p_cols_count), JSONB 'null'));

    IF (v_cur_metadata ->> 'from_refcursor') THEN
        v_cur_metadata := jsonb_set(v_cur_metadata, '{from_refcursor}', JSONB 'false');
    END IF;

    IF (v_statement_hash <> coalesce(v_cur_metadata ->> 'statement_hash', ''))
    THEN
        v_cur_metadata := jsonb_set(v_cur_metadata, '{statement_hash}', to_jsonb(v_statement_hash));

        v_cur_metadata := jsonb_set(v_cur_metadata, '{colinfo_refresh}', to_jsonb(1));

        v_sql_statement := aws_oracle_ext.strip_sql_comments(v_sql_statement,
                                                             p_adv_quote_tags => ARRAY['$q$','$Q$'],
                                                             p_validate_multiline => TRUE);
        UPDATE dbms_sql$cursor
           SET sql_statement = v_sql_statement
         WHERE cursor_id = p_cursor_id;
    END IF;

    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext',
                                                'dbms_sql',
                                                'last_parsed_cursor_id',
                                                p_cursor_id);

    PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext',
                                                'dbms_sql',
                                                format('cursor_id_%s_metadata', p_cursor_id),
                                                v_cur_metadata);
EXCEPTION
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    WHEN invalid_prepared_statement_definition THEN
        RAISE USING MESSAGE := format('SQL statement is empty. Nothing to associate with cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Supplied SQL statement is empty. Oracle error ''ORA-00900: invalid SQL statement''.',
                    HINT := 'Correct the statement and try to perform ''dbms_sql$parse'' procedure again.';
END;

DECLARE v_cur_metadata JSONB; v_curcols_data JSONB; v_cursor_id INTEGER; v_sql_statement TEXT; v_cols_count SMALLINT; v_col_aliases VARCHAR; v_colstype_block TEXT; v_prepdynsql_block TEXT; v_coldtype_cases VARCHAR; v_err_message TEXT; v_err_stack TEXT; v_refcur_rec RECORD; v_refcur_rec_json JSONB; v_debug_mode SMALLINT := 0; BEGIN IF (p_refcursor IS NULL) THEN RAISE invalid_cursor_state; END IF;
FETCH FIRST FROM p_refcursor INTO v_refcur_rec;
v_cursor_id := aws_oracle_ext.dbms_sql$open_cursor();
v_cols_count := aws_oracle_ext.regexp_count(regexp_replace(v_refcur_rec::TEXT, '"(.*?)"', 'x', 'gi'), ',') + 1;
v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', v_cursor_id));
v_cur_metadata := jsonb_set(v_cur_metadata, '{cols_count}', to_jsonb(v_cols_count));
v_colstype_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_refcur_rec RECORD;', format(' v_cursor_id INTEGER := %s;', v_cursor_id), format(' v_refcursor REFCURSOR := %s;', quote_literal(p_refcursor)), 'BEGIN', 'FETCH FIRST', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', '', '-- Creating temp table in order to reflect the columns structure', format('CREATE TEMP TABLE tbl_curid%s_colstype', v_cursor_id), 'WITH (OIDS = FALSE)', 'ON COMMIT DROP', 'AS', concat('SELECT ', (SELECT string_agg(concat('outer_block.v_refcur_rec.', col_name), concat(',', chr(10), repeat(' ', 7))) FROM json_object_keys(row_to_json(v_refcur_rec)) AS col_name)), 'WITH NO DATA;', '', concat_ws(chr(10), '-- Saving detailed column data (name, type, precision, etc.)', 'INSERT INTO dbms_sql$cursor_column (cursor_id,', ' column_pos,', ' col_name,', ' data_type,', ' base_type,', ' is_nullable,', ' chr_max_len,', ' chr_byte_len,', ' num_precision,', ' num_scale,', ' tstamp_precision,', ' interval_type,', ' interval_precision)', 'SELECT outer_block.v_cursor_id,', ' ordinal_position,', ' column_name,', ' upper(data_type) AS data_type,', ' CASE', ' WHEN data_type IN (''text'',', ' ''character'',', ' ''character varying'') THEN ''TEXT''', ' WHEN data_type IN (''real'',', ' ''bigint'',', ' ''integer'',', ' ''numeric'',', ' ''decimal'',', ' ''smallint'',', ' ''double precision'') THEN ''NUMERIC''', ' WHEN data_type IN (''date'',', ' ''timestamp without time zone'') THEN ''TIMESTAMP WITHOUT TIME ZONE''', ' ELSE upper(data_type)', ' END AS base_type,', ' CASE is_nullable', ' WHEN ''YES'' THEN TRUE', ' ELSE FALSE', ' END AS is_nullable,', ' character_maximum_length,', ' character_octet_length,', ' numeric_precision,', ' numeric_scale,', ' datetime_precision,', ' interval_type,', ' interval_precision', ' FROM information_schema.columns'), format(' WHERE table_name = ''tbl_curid%s_colstype''', v_cursor_id), ' ORDER BY ordinal_position ASC;', '', '-- Dropping temp table since its not needed anymore', format('DROP TABLE IF EXISTS tbl_curid%s_colstype;', v_cursor_id), 'END; $dbms_sql$', 'LANGUAGE plpgsql;');
EXECUTE v_colstype_block;
SELECT jsonb_object_agg(concat('col_', curcol_data.column_pos), curcol_data.column_data) INTO v_curcols_data FROM (SELECT curcol3.column_pos, (SELECT to_jsonb(curcol2.*) FROM (SELECT curcol1.data_type, curcol1.base_type FROM dbms_sql$cursor_column AS curcol1 WHERE curcol1.cursor_id = curcol3.cursor_id AND curcol1.column_pos = curcol3.column_pos ) AS curcol2 ) AS column_data FROM dbms_sql$cursor_column AS curcol3 WHERE curcol3.cursor_id = v_cursor_id ) AS curcol_data;
v_cur_metadata := jsonb_set(v_cur_metadata, '{curcols_data}', v_curcols_data);
v_cur_metadata := jsonb_set(v_cur_metadata, '{from_refcursor}', JSONB 'true');
PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', 'dbms_sql', format('cursor_id_%s_metadata', v_cursor_id), v_cur_metadata);
SELECT string_agg(coalesce(concat(' CASE colpos', chr(10)) || (SELECT string_agg(format(' WHEN %s THEN col_%s', colpos, colpos), chr(10)) FROM unnest(colpos_list) AS colpos) || concat(chr(10), ' END'), ' NULL'), concat(',', chr(10)) ORDER BY datatypes.dtype_order ASC) INTO v_coldtype_cases FROM (SELECT 'TEXT' AS base_type, 1 AS dtype_order UNION ALL SELECT 'NUMERIC', 2 UNION ALL SELECT 'MONEY', 3 UNION ALL SELECT 'TIMESTAMP WITHOUT TIME ZONE', 4 UNION ALL SELECT 'TIMESTAMP WITH TIME ZONE', 5 UNION ALL SELECT 'INTERVAL', 6 UNION ALL SELECT 'BOOLEAN', 7 UNION ALL SELECT 'XML', 8 UNION ALL SELECT 'BYTEA', 9) AS datatypes LEFT JOIN (SELECT base_type, array_agg(column_pos) AS colpos_list FROM dbms_sql$cursor_column WHERE cursor_id = v_cursor_id GROUP BY base_type) AS curcols ON (curcols.base_type = datatypes.base_type);
SELECT string_agg(concat(repeat(' ', 29), 'col_', colnum), concat(',', chr(10))) INTO v_col_aliases FROM generate_series(1, v_cols_count) AS colnum;
v_prepdynsql_block := concat_ws(chr(10), 'DO $dbms_sql$', '<<outer_block>>', 'DECLARE', ' v_cur_metadata JSONB;', ' v_refcur_rec RECORD;', format(' v_cursor_id INTEGER := %s;', v_cursor_id), format(' v_cols_count SMALLINT := %s;', v_cols_count), ' v_rows_fetched INTEGER := 0;', format(' v_refcursor REFCURSOR := %s;', quote_literal(p_refcursor)), 'BEGIN', 'LOOP', '-- Fetching row into the record variable', 'IF (outer_block.v_rows_fetched = 0) THEN', ' FETCH FIRST', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', 'ELSE', ' FETCH NEXT', ' FROM outer_block.v_refcursor', ' INTO outer_block.v_refcur_rec;', 'END IF;', '', '-- Exit when no more row to fetch', 'EXIT WHEN NOT FOUND;', 'outer_block.v_rows_fetched := outer_block.v_rows_fetched + 1;', '', '-- Inserting column value into "dbms_sql$recordset" table', 'EXECUTE', quote_literal(concat_ws(chr(10), 'INSERT INTO dbms_sql$recordset (cursor_id,', ' rownum,', ' column_pos,', ' chr_val,', ' num_val,', ' money_val,', ' tstamp_val,', ' tstamptz_val,', ' interval_val,', ' bool_val,', ' xml_val,', ' bin_val)', ' SELECT $1,', ' $2,', ' colpos,', v_coldtype_cases, ' FROM (SELECT generate_series(1, $3) AS colpos,', ' dynsql.*', concat(' FROM (SELECT ', (SELECT string_agg(concat('$', col_num), concat(',', chr(10), repeat(' ', 24))) FROM generate_series(4, v_cols_count + 3) AS col_num)), format(' ) AS dynsql (%s)', trim(v_col_aliases)), ' ) AS dynsql2')), 'USING outer_block.v_cursor_id,', ' outer_block.v_rows_fetched,', ' outer_block.v_cols_count,', concat((SELECT string_agg(concat(' outer_block.v_refcur_rec.', col_name), concat(',', chr(10))) FROM json_object_keys(row_to_json(v_refcur_rec)) AS col_name), ';'), 'END LOOP;', '', 'outer_block.v_cur_metadata := -- Getting cursor metadata (JSON)', ' aws_oracle_ext.get_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', ' format(''cursor_id_%s_metadata'', outer_block.v_cursor_id)); -- context "cursor_id_xxx"', '', '-- Setting "rows_count" (JSON) cursor property', 'outer_block.v_cur_metadata := jsonb_set(outer_block.v_cur_metadata, ''{rows_count}'', to_jsonb(outer_block.v_rows_fetched));', '', '-- Saving cursor metadata (JSON)', 'PERFORM aws_oracle_ext.set_package_variable(''aws_oracle_ext'', -- schema name', ' ''dbms_sql'', -- package name', ' format(''cursor_id_%s_metadata'', outer_block.v_cursor_id), -- context "cursor_id_xxx"', ' outer_block.v_cur_metadata); -- cursor metadata JSON', 'END; $dbms_sql$', 'LANGUAGE plpgsql;');
EXECUTE v_prepdynsql_block;
PERFORM aws_oracle_ext.set_package_variable('aws_oracle_ext', 'dbms_sql', 'last_parsed_cursor_id', v_cursor_id); SELECT statement INTO v_sql_statement FROM pg_catalog.pg_cursors WHERE name = p_refcursor::TEXT;
UPDATE dbms_sql$cursor SET sql_statement = v_sql_statement WHERE cursor_id = v_cursor_id;
IF (v_debug_mode = 1) THEN UPDATE dbms_sql$cursor SET colstype_block = v_colstype_block, prepdynsql_block = v_prepdynsql_block WHERE cursor_id = v_cursor_id; END IF;
RETURN v_cursor_id; EXCEPTION WHEN invalid_cursor_state THEN RAISE USING MESSAGE := 'Supplied REFCURSOR is not opened.', DETAIL := 'Could not transform a not OPENed REFCURSOR into a DBMS_SQL cursor number. Oracle error ''ORA-01001: invalid cursor''.', HINT := 'Perform OPEN operation over supplied REFCURSOR before passing it in.'; WHEN syntax_error THEN GET STACKED DIAGNOSTICS v_err_message = MESSAGE_TEXT, v_err_stack = PG_EXCEPTION_CONTEXT;
RAISE USING MESSAGE := 'Syntax error has occured while trying to transform REFCURSOR into a DBMS_SQL cursor number.', DETAIL := concat(v_err_message, '.'), HINT := concat('(Call Stack)', chr(10), v_err_stack); END;

DECLARE
    v_cur_metadata JSONB;
    v_curr_row INTEGER;
    v_rows_count INTEGER;
    v_coldtype_casts VARCHAR;
    v_colnames_cases VARCHAR;
    v_colnames_list VARCHAR;
    v_resrecset_sql TEXT;
    v_res_refcursor REFCURSOR := concat('cursor_id_', p_cursor_id, '_resrecset');
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open;
       - get_package_variable;
       - dbms_sql$close_cursor.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    END IF;

    -- Getting cursor metadata (JSON)
    v_cur_metadata := aws_oracle_ext.get_package_variable('aws_oracle_ext', -- schema name
                                                          'dbms_sql', -- package name
                                                          concat('cursor_id_', p_cursor_id, '_metadata')); -- context "cursor_id_xxx"
    -- Cursor rows count should not be empty (null)
    IF ((v_cur_metadata ->> 'rows_count') IS NULL) THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Getting column info (list of names, data type casts, column position cases)
    SELECT string_agg(concat('CAST("', col_name, '" AS ', data_type, ')'), concat(',', chr(10), repeat(' ', 7))),
           string_agg(concat('MAX(CASE column_pos WHEN ',
                      column_pos,
                      ' THEN ',
                      CASE base_type
                         WHEN 'TEXT' THEN 'chr_val'
                         WHEN 'NUMERIC' THEN 'num_val'
                         WHEN 'MONEY' THEN 'money_val'
                         WHEN 'TIMESTAMP WITHOUT TIME ZONE' THEN 'tstamp_val'
                         WHEN 'TIMESTAMP WITH TIME ZONE' THEN 'tstamptz_val'
                         WHEN 'INTERVAL' THEN 'interval_val'
                         WHEN 'BOOLEAN' THEN 'bool_val'
                         WHEN 'XML' THEN 'xml_val'
                         WHEN 'BYTEA' THEN 'bin_val'
                      END,
                      ' END)'), concat(',', chr(10), repeat(' ', 15))),
           string_agg(concat('"', col_name, '"'), ', ')
      INTO v_coldtype_casts,
           v_colnames_cases,
           v_colnames_list -- Comma-separated list of column names
      FROM dbms_sql$cursor_column
     WHERE cursor_id = p_cursor_id;

    v_resrecset_sql := concat('SELECT ', v_coldtype_casts, chr(10), repeat(' ', 2),
                              'FROM (SELECT ', v_colnames_cases, chr(10), repeat(' ', 10),
                              'FROM dbms_sql$recordset', chr(10), repeat(' ', 9),
                              'WHERE rownum > ', coalesce((v_cur_metadata ->> 'curr_row')::INTEGER, 0), chr(10), repeat(' ', 9),
                              'GROUP BY rownum', chr(10), repeat(' ', 7),
                              ') AS recset (', v_colnames_list, ')');

    -- Opening REFCURSOR for dymanic SQL query resulting data
    OPEN v_res_refcursor SCROLL FOR EXECUTE v_resrecset_sql;

    -- Closing the cursor and cleaning its context
    PERFORM aws_oracle_ext.dbms_sql$close_cursor(p_cursor_id);

    RETURN v_res_refcursor;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Cursor rows count should not be empty (null)
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Invalid cursor. Oracle error ''ORA-01001''.',
                    DETAIL := format('Dynamic SQL statement, associated with cursor_id (%s), was not executed.', p_cursor_id),
                    HINT := format('Perform ''dbms_sql$execute'' or ''dbms_sql$execute_and_fetch'' procedure for cursor_id (%s).', p_cursor_id);
END;

DECLARE
    v_var_info TEXT[];
    v_chr_val TEXT;
    v_num_val NUMERIC;
    v_money_val MONEY;
    v_tstamp_val TIMESTAMP WITHOUT TIME ZONE;
    v_tstamptz_val TIMESTAMP WITH TIME ZONE;
    v_interval_val INTERVAL;
    v_bool_val BOOLEAN;
    v_xml_val XML;
    v_bin_val BYTEA;
    v_is_fixed SMALLINT;
    v_value_size INTEGER;
    v_var_base_type VARCHAR;
    v_var_data_type VARCHAR;
    v_out_var_size INTEGER;
    v_out_base_type VARCHAR;
    v_out_data_type VARCHAR := upper(pg_typeof(p_var_value)::VARCHAR);
BEGIN
    /* Requires functions to be installed in "aws_oracle_ext" schema:
       - dbms_sql$is_open.
    */

    -- Exit from function if the cursor (cursor_id) is not opened
    IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN
        RAISE invalid_cursor_state;
    -- Variable name should not be empty (null)
    ELSEIF (p_var_name IS NULL) THEN
        RAISE null_value_not_allowed;
    END IF;

    -- Determining base data type of passed OUT variable
    v_out_base_type :=
        CASE
           WHEN v_out_data_type IN ('TEXT',
                                    'CHARACTER',
                                    'CHARACTER VARYING') THEN 'TEXT'
           WHEN v_out_data_type IN ('REAL',
                                    'BIGINT',
                                    'INTEGER',
                                    'NUMERIC',
                                    'DECIMAL',
                                    'SMALLINT',
                                    'DOUBLE PRECISION') THEN 'NUMERIC'
           WHEN v_out_data_type = 'MONEY' THEN 'MONEY'
           WHEN v_out_data_type IN ('DATE',
                                    'TIMESTAMP WITHOUT TIME ZONE') THEN 'TIMESTAMP WITHOUT TIME ZONE'
           WHEN v_out_data_type = 'TIMESTAMP WITH TIME ZONE' THEN 'TIMESTAMP WITH TIME ZONE'
           WHEN v_out_data_type = 'INTERVAL' THEN 'INTERVAL'
           WHEN v_out_data_type = 'BOOLEAN' THEN 'BOOLEAN'
           WHEN v_out_data_type = 'XML' THEN 'XML'
           WHEN v_out_data_type = 'BYTEA' THEN 'BYTEA'
        END;

    -- Extracting variable info (label in statement code and general name) from supplied string
    v_var_info := regexp_matches(p_var_name, '^((?:[[:space:]]*\$[[:digit:]]+[[:space:]]*(?:\|)?)+)*[[:space:]]*\:?([[:alnum:]_]+)[[:space:]]*$', 'gi');

    /* Extracting var data from "dbms_sql$bind_variable"
       (by cursor id and variable name)
    */
    SELECT data_type,
           base_type,
           value_size,
           is_fixed,
           chr_val,
           num_val,
           money_val,
           tstamp_val,
           tstamptz_val,
           interval_val,
           bool_val,
           xml_val,
           bin_val
      INTO STRICT
           v_var_data_type,
           v_var_base_type,
           v_value_size,
           v_is_fixed,
           v_chr_val,
           v_num_val,
           v_money_val,
           v_tstamp_val,
           v_tstamptz_val,
           v_interval_val,
           v_bool_val,
           v_xml_val,
           v_bin_val
      FROM dbms_sql$bind_variable
     WHERE cursor_id = p_cursor_id
       AND var_name = v_var_info[2]; -- Variable name

    -- Check if OUT argument base type matches declared bind variable base type
    IF (substring(v_out_base_type, 1, 14) != substring(v_var_base_type, 1, 14)) THEN
        RAISE datatype_mismatch;
    -- Variable should not be declared using "dbms_sql$bind_variable_char"
    ELSIF (v_is_fixed = 1) THEN
        RAISE most_specific_type_mismatch;
    END IF;

    -- Copying value into the OUT argument
    IF (v_out_base_type = 'TEXT') THEN
        v_chr_val := CASE
                        WHEN v_value_size IS NULL THEN v_chr_val
                        ELSE substring(v_chr_val, 1, v_value_size)
                     END;
        v_out_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);

        IF (v_out_data_type != 'CHARACTER') THEN
            p_var_value := v_chr_val;
        ELSIF (v_out_var_size != 0) THEN
            IF (v_out_var_size < coalesce(char_length(v_chr_val), 0)) THEN
                RAISE string_data_length_mismatch;
            END IF;

            p_var_value := rpad(v_chr_val, v_out_var_size, ' ');
        ELSE
            p_var_value := rpad(v_chr_val, v_var_size, ' ');
        END IF;
    ELSIF (v_out_base_type = 'NUMERIC') THEN
        p_var_value := v_num_val;
    ELSIF (v_out_base_type = 'MONEY') THEN
        p_var_value := v_money_val;
    ELSIF (v_out_base_type = 'TIMESTAMP WITHOUT TIME ZONE') THEN
        IF (v_var_base_type = 'TIMESTAMP WITH TIME ZONE') THEN
            p_var_value := v_tstamptz_val::TIMESTAMP WITHOUT TIME ZONE;
        ELSE
            p_var_value := v_tstamp_val;
        END IF;
    ELSIF (v_out_base_type = 'TIMESTAMP WITH TIME ZONE') THEN
        IF (v_var_base_type = 'TIMESTAMP WITHOUT TIME ZONE') THEN
            p_var_value := v_tstamp_val::TIMESTAMP WITH TIME ZONE;
        ELSE
            p_var_value := v_tstamptz_val;
        END IF;
    ELSIF (v_out_base_type = 'INTERVAL') THEN
        p_var_value := v_interval_val;
    ELSIF (v_out_base_type = 'BOOLEAN') THEN
        p_var_value := v_bool_val;
    ELSIF (v_out_base_type = 'XML') THEN
        p_var_value := v_xml_val;
    ELSIF (v_out_base_type = 'BYTEA') THEN
        p_var_value := substring(v_bin_val from 1 for v_value_size);
    END IF;
EXCEPTION
    -- Cursor isn't opened or doesn't exist
    WHEN invalid_cursor_state THEN
        RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id),
                    DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id);
    -- Variable name is empty (null)
    WHEN null_value_not_allowed THEN
        RAISE USING MESSAGE := 'Bind variable name is empty.',
                    DETAIL := 'Could not get a value of a variable without name. Oracle error ''ORA-01006''.',
                    HINT := 'Pass in an existing variable name in order to get it''s value.';
    -- Variable with supplied name isn't found
    WHEN no_data_found THEN
        RAISE USING MESSAGE := format('Variable %s doesn''t found for cursor_id (%s). Oracle error ''ORA-01006''.', quote_literal(v_var_info[2]), p_cursor_id),
                    DETAIL := format('Variable with name %s, binded to cursor_id (%s), doesn''t found or cursor_id is wrong.', quote_literal(v_var_info[2]), p_cursor_id),
                    HINT := format('Make sure that cursor_id (%s) exists and variable %s was previously binded to it trough ''dbms_sql$bind_variable procedure''.',
                                   p_cursor_id, quote_literal(v_var_info[2]));
    -- OUT argument base type doesn't match declared bind variable base type
    WHEN datatype_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared variable type, cursor_id (%s).', p_cursor_id),
                    DETAIL := format('OUT argument type %s, variable type %s. Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable''.',
                                     v_out_data_type, v_var_base_type),
                    HINT := format('Make sure that supplied OUT argument type match that for cursor_id (%s) variable.',
                                   p_cursor_id, quote_literal(v_var_info[2]));
    -- Variable was declared using "dbms_sql$bind_variable_char"
    WHEN most_specific_type_mismatch THEN
        RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared variable type, cursor_id (%s).', p_cursor_id),
                    DETAIL := 'Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable',
                    HINT := format('Try to use ''dbms_sql$variable_value_char'' procedure to get a %s variable value for cursor_id (%s).',
                                   quote_literal(v_var_info[2]), p_cursor_id);
    -- Length of the supplied OUT argument is too small
    WHEN string_data_length_mismatch THEN
        RAISE USING MESSAGE := 'Character string buffer too small. Oracle error ''ORA-06502''.',
                    DETAIL := 'Length of the supplied OUT argument is too small to store bind variable value.',
                    HINT := format('Supply OUT argument of proper length, minimum %s bytes to store binded variable value.',
                                   coalesce(char_length(v_chr_val), 0));
END;

DECLARE v_var_info TEXT[]; v_chr_val TEXT; v_is_fixed SMALLINT; v_var_size INTEGER; v_value_size INTEGER; v_var_data_type VARCHAR; v_out_var_size INTEGER; v_out_data_type VARCHAR := upper(pg_typeof(p_var_value)::VARCHAR); BEGIN /* Requires functions to be installed in "aws_oracle_ext" schema: - dbms_sql$is_open. */
-- Exit from function if the cursor (cursor_id) is not opened IF (NOT aws_oracle_ext.dbms_sql$is_open(p_cursor_id)) THEN RAISE invalid_cursor_state; -- Variable name should not be empty (null) ELSEIF (p_var_name IS NULL) THEN RAISE null_value_not_allowed; END IF;
-- Extracting variable info (label in statement code and general name) from supplied string v_var_info := regexp_matches(p_var_name, '^((?:[[:space:]]*\$[[:digit:]]+[[:space:]]*(?:\|)?)+)*[[:space:]]*\:?([[:alnum:]_]+)[[:space:]]*$', 'gi');
/* Extracting var data from "dbms_sql$bind_variable" (by cursor id and variable name) */ SELECT data_type, var_size, value_size, is_fixed, chr_val INTO STRICT v_var_data_type, v_var_size, v_value_size, v_is_fixed, v_chr_val FROM dbms_sql$bind_variable WHERE cursor_id = p_cursor_id AND var_name = v_var_info[2]; -- Variable name;
-- Variable should not be declared using "dbms_sql$bind_variable" IF (v_is_fixed != 1) THEN RAISE most_specific_type_mismatch; END IF;
v_chr_val := CASE WHEN v_value_size IS NULL THEN v_chr_val ELSE substring(v_chr_val, 1, v_value_size) END;
-- Copying value into the OUT argument IF (v_out_data_type IN ('TEXT', 'CHARACTER', 'CHARACTER VARYING')) THEN v_out_var_size := coalesce(length(p_var_value::BYTEA, pg_client_encoding()), 0);
IF (v_out_data_type != 'CHARACTER') THEN p_var_value := v_chr_val; ELSIF (v_out_var_size != 0) THEN IF (v_out_var_size < coalesce(char_length(v_chr_val), 0)) THEN RAISE string_data_length_mismatch; END IF;
p_var_value := rpad(v_chr_val, v_out_var_size, ' '); ELSE p_var_value := rpad(v_chr_val, v_var_size, ' '); END IF; ELSIF (v_out_data_type IN ('REAL', 'BIGINT', 'INTEGER', 'NUMERIC', 'DECIMAL', 'SMALLINT', 'DOUBLE PRECISION')) THEN p_var_value := trim(v_chr_val)::NUMERIC; ELSIF (v_out_data_type IN ('DATE', 'TIMESTAMP WITHOUT TIME ZONE', 'TIMESTAMP WITH TIME ZONE')) THEN p_var_value := CASE v_out_data_type WHEN 'DATE' THEN to_date(v_chr_val, 'DD-MON-YY') ELSE to_timestamp(v_chr_val, 'DD-MON-YY HH24:MI:SS.US') END; ELSIF (v_out_data_type = 'INTERVAL') THEN p_var_value := v_chr_val::INTERVAL; END IF; EXCEPTION -- Cursor isn't opened or doesn't exist WHEN invalid_cursor_state THEN RAISE USING MESSAGE := format('Cursor_id (%s) does not exist or incorrect.', p_cursor_id), DETAIL := format('Cursor_id (%s) is not found, SQL statement text was not associated with it.', p_cursor_id), HINT := format('Make sure that cursor_id (%s) exists or perform ''dbms_sql$open_cursor'' procedure to get a new one.', p_cursor_id); -- Variable name is empty (null) WHEN null_value_not_allowed THEN RAISE USING MESSAGE := 'Bind variable name is empty.', DETAIL := 'Could not get a value of a variable without name. Oracle error ''ORA-01006''.', HINT := 'Pass in an existing variable name in order to get it''s value.'; -- Variable with supplied name isn't found WHEN no_data_found THEN RAISE USING MESSAGE := format('Variable %s doesn''t found for cursor_id (%s). Oracle error ''ORA-01006''.', quote_literal(v_var_info[2]), p_cursor_id), DETAIL := format('Variable with name %s, binded to cursor_id (%s), doesn''t found or cursor_id is wrong.', quote_literal(v_var_info[2]), p_cursor_id), HINT := format('Make sure that cursor_id (%s) exists and variable %s was previously binded to it trough ''dbms_sql$bind_variable_char'' procedure.', p_cursor_id, quote_literal(v_var_info[2])); -- Variable was declared using "dbms_sql$bind_variable" WHEN most_specific_type_mismatch THEN RAISE USING MESSAGE := format('Type of OUT argument doesn''t match declared variable type, cursor_id (%s).', p_cursor_id), DETAIL := 'Oracle error ''ORA-06562: type of OUT argument must match type of column or bind variable.', HINT := format('Try to use ''dbms_sql$variable_value'' procedure to get a %s variable value for cursor_id (%s).', quote_literal(v_var_info[2]), p_cursor_id); -- Length of the supplied OUT argument is too small WHEN string_data_length_mismatch THEN RAISE USING MESSAGE := 'Character string buffer too small. Oracle error ''ORA-06502''.', DETAIL := 'Length of the supplied OUT argument is too small to store bind variable value.', HINT := format('Supply OUT argument of proper length, minimum %s bytes to store binded variable value.', coalesce(char_length(v_chr_val), 0)); -- Possible data type conversion error WHEN invalid_datetime_format THEN RAISE USING MESSAGE := 'Error during column value conversion (from char to datetime).', DETAIL := format('Value %s is not correct for conversion to datetime. Cursor id (%s), column (%s).', quote_literal(v_chr_val), p_cursor_id, p_column_pos); -- Possible data type conversion error WHEN invalid_xml_content THEN RAISE USING MESSAGE := 'Error during column value conversion (from char to XML document).', DETAIL := format('Value %s is not correct for conversion to XML document. Cursor id (%s), column (%s).', quote_literal(v_chr_val), p_cursor_id, p_column_pos); -- Possible data type conversion error WHEN invalid_text_representation THEN RAISE USING MESSAGE := 'Error during column value conversion (from char to numeric).', DETAIL := format('Value %s is not correct for conversion to numeric. Cursor id (%s), column (%s).', quote_literal(v_chr_val), p_cursor_id, p_column_pos); END;

DECLARE
    v_server_pid TEXT;
    v_obj_handle TEXT;
    v_schema_name TEXT;
    v_routine_name TEXT;
    v_routine_type TEXT;
    v_routine_oid TEXT;
    i_param_type TEXT;
    v_call_stack TEXT[];
    v_call_info TEXT[];
    v_param_types TEXT;
    v_stack_header TEXT;
    v_search_path TEXT[];
    v_pg_call_stack TEXT;
    v_anonymous_block TEXT;
    v_call_stack_rec TEXT;
    v_full_search_path TEXT[];
    i_call_stack_rec RECORD;
BEGIN
    -- Determining call stack label depending on Oracle style param
    v_stack_header := CASE
                         WHEN p_ora_style THEN '------- PL/SQL Call Stack -------'
                         ELSE '------ PL/pgSQL Call Stack ------'
                      END;

    -- Determining anonymous block label depending on Oracle style param
    v_anonymous_block := CASE
                            WHEN p_ora_style THEN 'anonymous block'
                            ELSE 'inline_code_block'
                         END;

    -- Constructing call stack header
    v_call_stack := ARRAY[v_stack_header,
                          '    object       line  object',
                          '    handle     number  name'];

    -- Obtaining current search path
    v_search_path := regexp_split_to_array(replace(current_setting('search_path'),
                                                   '$user', current_user), '\s*,\s*');
    -- Getting server process ID
    v_server_pid = pg_backend_pid();

    -- Getting current call stack
    GET DIAGNOSTICS v_pg_call_stack = PG_CONTEXT;

    FOR i_call_stack_rec IN
    (SELECT t.*
       FROM regexp_split_to_table(v_pg_call_stack, chr(10))
       WITH ORDINALITY AS t(call_stack, line_num))
    LOOP
        CONTINUE WHEN i_call_stack_rec.line_num = 1 OR
                      i_call_stack_rec.call_stack !~ 'line \d+ at';

        -- Obtaining info regarding particular stack call
        v_call_info := regexp_matches(i_call_stack_rec.call_stack,
                                      '^[[:alnum:]_/]+\s+([a-z]+)\s+(.*)(?<=[^\s])\s+line\s+(\d+)', 'g');
        v_routine_oid := NULL;

        v_routine_type := v_call_info[1];

        -- Extracting param types list from routine signature
        v_param_types := substring(v_call_info[2], '\((.*)\)$');

        -- Extracting schema name from `schema.["]routine["]`
        v_schema_name := substring(v_call_info[2], '^(.+)\.');

        -- Extracting routine name from `schema.["]routine["]`(params)
        v_routine_name := regexp_replace(v_call_info[2], '^(.+)\.|\((.*)\)$', '', 'g');

        -- Emulating Oracle anonymous block label
        IF (p_ora_style) THEN
            v_routine_name := replace(v_routine_name, 'inline_code_block', 'anonymous block');
        END IF;

        -- Determining whether call routine is a function or a procedure
        IF (v_routine_type = 'function' AND v_routine_name != 'anonymous block')
        THEN
            -- Putting stack record schema name on top of search path
            v_full_search_path := CASE
                                     WHEN v_schema_name IS NULL THEN v_search_path
                                     ELSE array_prepend(v_schema_name, v_search_path)
                                  END;
            SELECT oid,
                   CASE prokind
                      WHEN 'p' THEN 'procedure'
                      ELSE 'function'
                   END,
                   pronamespace::REGNAMESPACE::TEXT
              INTO v_routine_oid,
                   v_routine_type,
                   v_schema_name
              FROM pg_catalog.pg_proc
             WHERE proname = substring(v_routine_name, '"?([^"]+)') -- routine name
               AND pronamespace::REGNAMESPACE::TEXT = ANY(v_full_search_path) -- schema names list
               AND proargtypes = coalesce(array_to_string(string_to_array(v_param_types, ',')::REGTYPE[]::OID[],
                                          ' '), '')::OIDVECTOR -- param OIDs list
             ORDER BY array_position(v_full_search_path, pronamespace::REGNAMESPACE::TEXT) ASC
             LIMIT 1;
        END IF;

        -- Constructing object handle (server_PID.object_OID)
        v_obj_handle := concat(v_server_pid, '.' || v_routine_oid);

        -- Constructing call stack record
        v_call_stack_rec :=
            concat(rpad(repeat(' ', (14 - char_length(v_obj_handle)) / 2) || v_obj_handle, 14), -- object handle
                   ' ', -- delimiter
                   lpad(v_call_info[3], 6, ' '), -- line number
                   '  ', -- delimiter
                   CASE
                      WHEN v_routine_name !~ '^(anonymous\s|inline_code_)block$'
                      THEN v_routine_type || ' ' -- routine type
                   END,
                   CASE
                      WHEN v_schema_name ~ '^"' THEN v_schema_name
                      ELSE CASE
                              WHEN p_ora_style THEN upper(v_schema_name)
                              ELSE v_schema_name
                           END
                   END || '.', -- schema name
                   CASE
                      WHEN v_routine_name ~ '^"' OR
                           v_routine_name ~ '^(anonymous\s|inline_code_)block$'
                      THEN v_routine_name
                      ELSE CASE
                              WHEN p_ora_style THEN upper(v_routine_name)
                              ELSE v_routine_name
                           END
                   END, -- routine name
                   CASE
                      WHEN v_routine_name !~ '^(anonymous\s|inline_code_)block$'
                      THEN CASE
                              WHEN NOT p_ora_style THEN concat('(', v_param_types, ')')
                              ELSE upper('(' || nullif(v_param_types, '') || ')')
                           END
                   END);

        v_call_stack := array_append(v_call_stack, v_call_stack_rec);
    END LOOP;

    -- SQL SELECT dbms_utility$format_call_stack()
    IF (array_length(v_call_stack, 1) = 3) THEN
        v_call_stack :=
            array_append(v_call_stack,
                         rpad(repeat(' ', (14 - char_length(v_server_pid)) / 2) || v_server_pid, 14)
                         || '      1  '
                         || v_anonymous_block);
    END IF;

    RETURN array_to_string(v_call_stack, chr(10));
END;

if (typeof(require) !== 'function') {
  const initColl = plv8.find_function("aws_oracle_ext.plv8_require");
  initColl();
}
  return Date.now() / 10;

declare
	lCtx aws_oracle_ext.dbms_xmlgen$tContext;
	lnCtxHdl int := trunc(ctx);
begin
    lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);

    if not lCtx is null then
        perform aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => lCtx.ctxIdx,
                                                       ctxCursor => null,
                                                       ctxQuery => null,
                                                       ctxAttributes => null,
                                                       ctxNumRowsProcessed => null
                                                      );
    end if;
end;

select *
           from json_populate_record(null::dbms_xmlgen$TctxAttributes,
                                     coalesce(ctxAttr, dbms_xmlgen$contextAttr2text())::json
                                    )

declare
	escChars text[5][2] := array[array['&quot;', '"'], array['&apos;', ''''], array['&lt;', '<'], array['&gt;', '>'], array['&amp;', '&']];
    i int;
    lvResult text;
begin
	if flag >= aws_oracle_ext.dbms_xmlgen$entity_decode() then
		begin
			lvResult := xmlData;
			for i in 1 .. 5 loop
				lvResult := replace(lvResult, escChars[i][1], escChars[i][2]);
			end loop;
   	    exception when others then
			lvResult := null;
		    raise;
		end;
	elsif flag = aws_oracle_ext.dbms_xmlgen$entity_encode() then
		lvResult := xmlserialize(content (xpath('.', xmlelement(name dummy, xmlData), array[array['xsi', 'http://www.w3.org/2001/XMLSchema-instance']]))[1] as text);
	else
		raise exception 'XML processing error. Nagative value [%] was readed', flag using hint = '22063';
	end if;
    return lvResult;
end;

begin
	if ctxHdl < 0  then
		raise exception 'XML processing error. Nagative value [%] was readed', ctxHdl using hint = '22063';
	end if;

	return aws_oracle_ext.dbms_xmlgen$context2obj(ctxHdl => trunc(ctxHdl));

end;

declare
	lCtx aws_oracle_ext.dbms_xmlgen$tContext;
	lnCtxHdl int := trunc(ctx);
begin
	lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);
	return coalesce(lCtx.ctxNumRowsProcessed, 0);
end;

declare
    lCtx aws_oracle_ext.dbms_xmlgen$tContext;
    lnCtxHdl int := trunc(ctx);
    lvReturn text;
    lrResult record;
begin
	lCtx := aws_oracle_ext.dbms_xmlgen$getctxobj(ctxHdl => lnCtxHdl);

	if lCtx is null then
	    raise exception 'Invalid context or doesn''t exists' using hint = '19019';
	else
		lrResult := aws_oracle_ext.dbms_xmlgen$qc2xml(pvSQL => lCtx.ctxQuery,
	                                                  pCr => lCtx.ctxCursor,
	                                                  pnMaxRows => (lCtx.ctxAttributes).ctxMaxRows,
	                                                  pvRowsetTag => (lCtx.ctxAttributes).ctxRowsettag,
	                                                  pvRowTag => (lCtx.ctxAttributes).ctxRowTag,
	                                                  pnSkipRows => (lCtx.ctxAttributes).ctxSkipRows
	                                                 );

	    perform aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => lCtx.ctxIdx,
	                                                   ctxCursor => lCtx.ctxCursor,
	                                                   ctxQuery => lCtx.ctxQuery,
	                                                   ctxAttributes => lCtx.ctxAttributes,
	                                                   ctxNumRowsProcessed => lrResult.pnRowCount,
	                                                   forceSetValues => true
	                                                  );

	    lvReturn := xmlserialize(document lrResult.pxXML as text
                                );
	end if;

	return lvReturn;
end;

begin
    return xmlserialize(document (aws_oracle_ext.dbms_xmlgen$qc2xml(pvSQL => sqlQuery,
				                                                    pCr => null,
				                                                    pvRowsetTag => aws_oracle_ext.dbms_xmlgen$default_rowsettag(),
				                                                    pvRowTag => aws_oracle_ext.dbms_xmlgen$default_rowtag()
				                                                   )).pxXML as text
                       );
end;

begin
	return aws_oracle_ext.dbms_xmlgen$setctxvalue(ctxHdl => null,
			                                      ctxCursor => queryString,
			                                      ctxQuery => null,
			                                      ctxAttributes => null,
			                                      ctxNumRowsProcessed => null
			                                     );
end;

