    when ip_unit = 'SRS_YARD'                       then op_dist := op_dist/0.914398414616029;
    when ip_unit = 'FATHOM'                          then op_dist := op_dist/1.8288;
    when ip_unit = 'British foot (1936)'              then op_dist := op_dist/0.3048007491;
  end case;
 end if; 
